insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 63, 'D', 'Estupro de vulnerável (Art. 217-A, do CP).');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(10, 64, 'Jane, dirigindo seu veículo dentro do limite de velocidade para a via, ao efetuar manobra em uma rotatória, acaba abalroando o carro de Lorena, que, desrespeitando as regras de trânsito, ingressou na rotatória enquanto Jane fazia a manobra. Em virtude do abalroamento, Lorena sofreu lesões corporais. Nesse sentido, com base na teoria da imputação objetiva, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 64, 'A', 'Jane não praticou crime, pois agiu no exercício regular de direito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 64, 'B', 'Jane não responderá pelas lesões corporais sofridas por Lorena com base no princípio da intervenção mínima.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 64, 'C', 'Jane não pode ser responsabilizada pelo resultado com base no princípio da confiança.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
