insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(10, 65, 'João está sendo processado por um crime doloso contra a vida e, após o oferecimento das alegações finais, o magistrado impronuncia o réu. Assinale a alternativa que apresenta a situação em que seria possível processar João novamente pelo mesmo fato delituoso.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 65, 'A', 'Desde que haja novas provas e não tenha ocorrido qualquer causa extintiva de punibilidade, pois a decisão de impronúncia não transita em julgado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 65, 'B', 'A justiça já se manifestou em relação ao processo de João, tendo a decisão do magistrado transitado em julgado.');
