insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(10, 66, 'Na cidade “A”, o Delegado de Polícia instaurou inquérito policial para averiguar a possível ocorrência do delito de estelionato praticado por Márcio, tudo conforme minuciosamente narrado na requisição do Ministério Público Estadual. Ao final da apuração, o Delegado de Polícia enviou o inquérito devidamente relatado ao Promotor de Justiça. No entendimento do parquet, a conduta praticada por Márcio, embora típica, estaria prescrita. Nessa situação, o Promotor deverá', 'A');
