insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(10, 68, 'No curso de uma investigação policial que apurava a ocorrência dos delitos de sonegação fiscal e evasão de divisas, o Procurador da República "X" requereu ao Juízo Federal Criminal medida assecuratória, já que obteve documentos que informavam os bens que teriam sido adquiridos pelo investigado com proventos da infração penal. O Juiz Federal decretou a medida assecuratória, que foi cumprida a contento. A partir do caso apresentado, assinale a alternativa que indica a medida assecuratória adotada.', 'A');
