insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(10, 71, 'Fernanda é contratada pela empresa Master, a título temporário, com base na Lei n. 6.019/74, pelo prazo certo de 3 meses. Quando do término deste período e ciente de que o empregador não pretende renovar o contrato, ela informa que se encontra grávida de 6 semanas. A respeito do caso proposto, de acordo com o entendimento do TST, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 71, 'A', 'Fernanda pode ter o contrato extinto porque o pacto foi feito a termo, de modo que no seu implemento a ruptura se impõe.');
