values(10, 71, 'B', 'Fernanda não poderá ser dispensada, pois, em razão da gravidez, possui garantia no emprego, mesmo sendo o contrato a termo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 71, 'C', 'Fernanda poderá ser desligada porque a natureza jurídica da ruptura não será resilição unilateral, mas caducidade do contrato.');
