insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(10, 72, 'Uma empresa contrata plano de saúde para os seus empregados, sem custo para os mesmos, com direito de internação em quarto particular. Posteriormente, estando em dificuldade financeira, resolve alterar as condições do plano para uso de enfermaria coletiva, em substituição ao quarto particular. Após a alteração, um empregado é contratado, passa mal e exige da empresa sua internação em quarto particular. Diante dessa situação, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(10, 72, 'A', 'O empregado está correto, pois não pode haver alteração contratual que traga malefício ao trabalhador, como foi o caso.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
