insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(13, 1, 'O advogado Carlos pretende substabelecer os poderes que lhe foram conferidos pelo seu cliente Eduardo, sem reserva de poderes, pois pretende realizar uma longa viagem, sem saber a data do retorno, não pretendendo manter compromissos profissionais. Nos termos das normas do Código de Ética, tal ato deve', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
