insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 1, 'B', 'ser comunicado ao cliente de modo inequívoco.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 1, 'C', 'ser realizado por tempo determinado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
