insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(13, 2, 'Sobre o prazo para ajuizamento de ação de cobrança de honorários de advogado, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 2, 'A', 'Prescreve em dois anos a ação de cobrança de honorários de advogado, contando-se o prazo do vencimento do contrato, se houver.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 2, 'B', 'Prescreve em cinco anos a ação de cobrança de honorários de advogado, contando-se o prazo do trânsito em julgado da decisão que os fixar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 2, 'C', 'Prescreve em dois anos a ação de cobrança de honorários de advogado, contando-se o prazo da ultimação do serviço extrajudicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 2, 'D', 'Prescreve em cinco anos a ação de cobrança de honorários de advogado, contando-se o prazo da decisão que os fixar, independentemente do seu trânsito em julgado.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(13, 3, 'Maria, após vários anos de tramitação de ação indenizatória em que figurava como autora, decidiu substituir José, advogado que até então atuava na causa, por João, amigo da família, que não cobraria honorários de nenhuma espécie de Maria. Ao final da ação, quando Maria finalmente recebeu os valores que lhe eram devidos, a título de indenização, foi procurada por José, que desejava receber honorários pelos serviços advocatícios prestados até o momento em que foi substituído. Sobre a hipótese sugerida, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 3, 'A', 'José tem direito a receber a integralidade dos honorários contratuais e de sucumbência, como se tivesse atuado na causa até o final, uma vez que foi substituído por vontade da cliente e não sua.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 3, 'B', 'José não tem direito a receber honorários, porque não atuou na causa até o seu fim.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 3, 'C', 'José tem direito a receber honorários contratuais, mas não tem direito a receber honorários de sucumbência.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 3, 'D', 'José tem direito a receber honorários contratuais, bem como honorários de sucumbência, calculados proporcionalmente, em face do serviço efetivamente prestado.');
