insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(13, 5, 'Agnaldo é advogado na área de Direito de Empresas, tendo como uma de suas clientes a sociedade Cobradora Eficiente Ltda., que consegue realizar os seus atos de cobrança com rara eficiência. Por força de sua atividade, a sociedade é convidada a participar de reunião com a Associação dos Consumidores Unidos e envia o seu advogado para dialogar com a referida instituição. Consoante o Estatuto da Advocacia, deve o advogado comparecer', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 5, 'A', 'à reunião com seu cliente, responsável pela empresa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 5, 'B', 'desacompanhado, com procuração com poderes ad juditia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 5, 'C', 'à reunião, com mandato outorgado com poderes especiais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(13, 5, 'D', 'ao local sem a presença do cliente e sem mandato.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(13, 6, 'Abel, por força de suas atividades como advogado, comparece à audiência designada para ocorrer às 13 horas. Aguarda algum tempo, mas não recebe qualquer notícia do início dos trabalhos forenses. Nesse caso, consoante o Estatuto da Advocacia, protocolizando comunicação em juízo, pode retirar-se do recinto passados', 'A');
