insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(15, 1, 'Abelardo é magistrado vinculado ao Tribunal de Justiça do Estado K e requer licença para tratamento de questões particulares, pelo prazo de três anos, o que foi deferido. Como, antes de assumir o referido cargo, era advogado regularmente inscrito nos quadros da OAB, requer o seu reingresso, comprovando o afastamento das funções judicantes. Nos termos do Estatuto da Advocacia, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(15, 1, 'A', 'A incompatibilidade com a advocacia persiste aposentadoria do cargo efetivo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(15, 1, 'B', 'O afastamento temporário do cargo que gera a incompatibilidade permite inscrição provisória.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(15, 1, 'C', 'A incompatibilidade permanece mesmo que ocorra o afastamento temporário do cargo.');
