insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(15, 75, 'Luiz Henrique é professor de Direito Constitucional e, durante o período letivo, precisará se afastar por dois meses para submeter-se a uma delicada cirurgia de emergência. Em razão disso, a faculdade contratou um professor substituto por esse período, valendo-se de uma empresa de contrato temporário. Diante da situação apresentada, c jurisprudência consolidada do TST, assinale a afirmativa correta.', 'A');
