insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 6, 'Pedro, em determinado momento, recebeu uma proposta de Antônio, colega de colégio, que se propôs a agenciar a indicação de novos clientes, mediante pagamento de comissão, a ser retirada dos honorários cobrados aos clientes, nos moldes da prática desenvolvi comercial. Com base no caso relatado, observadas as regras do Estatuto da OAB, assinale a afirmativa correta.', 'A');
