insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 7, 'O advogado Antônio participava do julgamento de recurso de apelação por ele interposto. Ao proferir seu voto, o Relator acusou o advogado Antônio de ter atuado de forma antiética e de ter tentado induzir os julgadores a erro. Em seguida, com o objetivo de se defender das acusações que lhe haviam sido dirigidas, Antônio solicitou usar da palavra, pela ordem, por mais cinco minutos, pleito que veio a ser indeferido pelo Presidente do órgão julgador. A respeito do direito de Antônio usar a palavra novamente, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 7, 'A', 'Não é permitido o uso da palavra por advogado em julgamentos de recursos de apelação.');
