insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 53, 'Daniel possui uma pequena mercearia e costuma aceitar cheques de seus clientes, como forma de pagamento. Ocorre que, no último mês, três dos cheques apresentados no prazo foram devolvidos por insuficiência de fundos. Daniel não obteve êxito na cobrança amigável, não lhe restando, portanto, outra alternativa senão recorrer ao Com base nessa situação hipotética, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 53, 'A', 'Daniel pode cumular várias execuções, sendo o mesmo devedor, ainda que fundadas em títulos diferentes e diversa a forma do processo, desde que o juízo seja competente para todas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 53, 'B', 'É vedado ao juiz examinar de ofício os requisitos que autorizam a cumulação de execuções.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 53, 'C', 'Daniel pode cumular várias execuções, fundadas em títulos diferentes, ainda que diversos os devedores, desde que para todas elas seja competente o juízo e do processo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 53, 'D', 'Daniel pode cumular várias execuções, sendo o mesmo devedor, ainda que fundadas em títulos diversos, desde que seja competente o juízo e haja identidade na forma do processo. 16 AME DE ORDEM UNIFICADO – TIPO 01 – BRANCA do autor até 5 Poder Judiciário. idêntica a forma');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 54, 'O processo eletrônico disciplinado pela Lei nº 11.419/2006 vem sendo implementado em larga escala no território nacional, com o propósito de conferir maior celeridade e proporcionar economia processual. normatizando internamente algumas questões peculiares no que tange a essa sistemática virtual da prestação jurisdicional, conforme vão surgindo controvérsias procedimentais. Entretanto, alguns pontos são claros e precisos no texto legal. A esse respeito, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 54, 'A', 'os atos processuais por meio realizados no dia e na hora de seu envio ao sistema do Poder Judiciário, motivo pelo qual, para atender o prazo processual, as petições eletrônicas serão consideradas tempestivas se enviadas nos dias úteis, até as 20 (vinte) horas, nos termos estabelecidos no Código de Processo Civil.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 54, 'B', 'os documentos produzidos eletronicamente, atendidas as formalidades impostas por lei, serão considerados originais para todos os efeitos legais, e qualquer arguição de falsidade do documento origina obrigatoriamente processada na forma de processo físico, sem suspensão do processo eletrônico.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 54, 'C', 'os autos de processos eletrônicos somente poderão ser remetidos a outro juízo se houver sistema compatível, sendo expressamente vedada a conversão do eletrônico em material impresso em papel e a nova autuação, salvo se de natureza criminal ou trabalhista.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 54, 'D', 'os atos judiciais publicados eletronicamente substituem qualquer outro meio de publicação oficial para efeito legal, salvo os casos em que, po ser realizadas a intimação ou a vista pessoal, ou em casos excepcionais e urgentes que justifiquem a realização do ato processual por outro meio determinado pelo juiz, considerando-se como data da publicação eletrônica o primeiro dia útil seguinte ao da sua disponibilização, dando-se início ao prazo processual no primeiro dia útil seguinte à data da publicação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 55, 'Rodolfo ingressou com ação rescisória de sentença prolatada em ação de cobrança, fundada na obtenção, após a sentença, de documento novo capaz de lhe assegurar pronunciamento favorável. Entretanto, o pedido foi julgado Tribunal de Justiça, por acórdão não unânime. A maioria dos julgadores entendeu que a parte sabia da existência do documento apresentado como novo e não conseguiu demonstrar o motivo de sua não utilização na ação original. Assinale a opção que contém o(s) recurso(s) o referido provimento jurisdicional.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 55, 'A', 'Embargos infringentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 55, 'B', 'Recursos especial e extraordinário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
