insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 66, 'Melinda Cunha foi denunciada pela prática bigamia. Ocorre que existe ação em curso no juízo cível onde se discute a validade do primeiro casamento celebrado pela denunciada. Entendendo o magistrado penal que a existência da infração penal depende da solução da controvérsia no juízo cível e que esta é séria e fundada, estaremos diante de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 66, 'A', 'prejudicial obrigatória, o que levará à suspensão do processo criminal e do prazo prescricional.');
