insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(16, 67, 'Juan da Silva foi autor de uma contravenção penal, em detrimento dos interesses da Caixa Econômica Federal, empresa pública. Praticou, ainda, outra contravenção em conexão, dessa vez em detrimento dos bens do Banco do Brasil, sociedade de economia mista. Dessa forma, para julgá-lo será competente', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 67, 'A', 'a Justiça Estadual, pelas duas infrações.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 67, 'B', 'a Justiça Federal, no caso da contravenção praticada em detrimento da Caixa Econômica Federal, e Justiça Estadual, no caso da infração em detrimento do Banco do Brasil.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(16, 67, 'C', 'a Justiça Federal, pelas duas infrações.');
