values(17, 'https://dpmzos25m8ivg.cloudfront.net/616/19072015182947_CADERNO_TIPO_1_XVII_EXAME.pdf', 'https://dpmzos25m8ivg.cloudfront.net/616/21082015081947_GABARITOS_DEFINITIVOS_XVII_EXAME_DE_ORDEM.pdf', 'Sun Jul 19 00:00:00 BRT 2015');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 1, 'Leôncio é estagiário de escritório especializado na área cível e testemunha o descumprimento de norma legal por funcionário público, imediatamente comunicando a situação ao seu advogado supervisor. Ambos dirigem-se ao órgão diretor administrativo competente e reclamam pelo descumprimento de lei, o que foi reduzido a termo. A referida reclamação veio a ser sumariamente arquivada por não ter sido feita na forma escrita. Nos termos do Estatuto da Advocacia, reclamações por descumprimento de lei', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 1, 'A', 'devem ser necessariamente escritas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 1, 'B', 'devem ser formuladas pela OAB, exclusivamente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 1, 'C', 'podem ser verbais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 1, 'D', 'são de atribuição privativa de Conselheiro da OAB.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 2, 'Os atos e contratos constitutivos de pessoas jurídicas, para sua admissão em registro, em não se tratando de empresas de pequeno porte e de microempresas, consoante o Estatuto da Advocacia, devem', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 2, 'A', 'apresentar os dados do contador responsável.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 2, 'B', 'permitir a participação de outros profissionais liberais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 2, 'C', 'conter o visto do advogado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 2, 'D', 'indicar o advogado que representará a sociedade.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 3, 'Patrícia foi aprovada em concurso público e tomou posse como Procuradora do Município em que reside. Como não pretendia mais exercer a advocacia privada, mas apenas atuar como Procuradora do Município, pediu o cancelamento de sua inscrição na OAB. A partir da hipótese apresentada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 3, 'A', 'Patrícia não agiu corretamente, pois os advogados públicos estão obrigados à inscrição na OAB para o exercício de suas atividades.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 3, 'B', 'Patrícia não agiu corretamente, pois deveria ter requerido apenas o licenciamento do exercício da advocacia e não o cancelamento de sua inscrição.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 3, 'C', 'Patrícia poderia ter pedido o licenciamento do exercício da advocacia, mas nada a impede de pedir o cancelamento de sua inscrição, caso não deseje mais exercer a advocacia privada.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 3, 'D', 'Patrícia agiu corretamente, pois, uma vez que os advogados públicos não podem exercer a advocacia privada, estão obrigados a requerer o cancelamento de suas inscrições.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 4, 'A advogada Maria foi presa em flagrante por furto cometido no interior de uma loja de departamentos. Na Delegacia, teve a assistência de advogado por ela constituído. O auto de prisão foi lavrado sem a presença de representante da Ordem dos Advogados do Brasil, fato que levou o advogado de Maria a arguir sua nulidade. Sobre a hipótese, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 4, 'A', 'O auto de prisão em flagrante não é nulo, pois só é obrigatória a presença de representante da OAB quando a prisão decorre de motivo ligado ao exercício da advocacia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 4, 'B', 'O auto de prisão em flagrante não é nulo, pois a presença de representante da OAB é facultativa em qualquer caso, podendo sempre ser suprida pela presença de advogado indicado pelo preso.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 4, 'C', 'O auto de prisão em flagrante é nulo, pois advogados não podem ser presos por crimes afiançáveis.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 4, 'D', 'O auto de prisão em flagrante é nulo, pois a presença de representante da OAB em caso de prisão em flagrante de advogado é sempre obrigatória.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 5, 'O advogado Márcio, sócio de determinado escritório de advocacia, contratou novos advogados para a sociedade e substabeleceu, com reserva em favor dos novos contratados, os poderes que lhe haviam sido outorgados por diversos clientes. O mandato possuía poderes para substabelecer. Um dos clientes do escritório, quando percebeu que havia novos advogados trabalhando na causa, os quais não eram por ele conhecidos, não apenas resolveu contratar outro escritório para atuar em sua demanda como ofereceu representação disciplinar contra Márcio, afirmando que o advogado não agira com lealdade e honestidade. A esse respeito, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 5, 'A', 'A representação oferecida não deve ser enquadrada como infração disciplinar, pois apenas o substabelecimento do mandato sem reserva de poderes deve ser comunicado previamente ao cliente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 5, 'B', 'A representação oferecida não deve ser enquadrada como infração disciplinar, pois o substabelecimento do mandato, com ou sem reserva de poderes, é ato pessoal do advogado da causa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 5, 'C', 'A representação oferecida deve ser enquadrada como infração disciplinar, pois o substabelecimento do mandato, com ou sem reserva de poderes, deve ser comunicado previamente ao cliente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 5, 'D', 'A representação oferecida deve ser enquadrada como infração disciplinar, pois o advogado deve avisar previamente ao cliente acerca de todas as petições que apresentará nos autos do processo, inclusive sobre as de juntada de substabelecimentos.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 6, 'Deise é uma próspera advogada e passou a buscar novos desafios, sendo eleita Deputada Estadual. Por força de suas raras habilidades políticas, foi eleita integrante da Mesa Diretora da Assembleia Legislativa do Estado Z. Ao ocupar esse honroso cargo procurou conciliar sua atividade parlamentar com o exercício da advocacia, sendo seu escritório agora administrado pela filha. Nos termos do Estatuto da Advocacia, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 6, 'A', 'A atividade parlamentar de Deise é incompatível com o exercício da advocacia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 6, 'B', 'A participação de Deise na Mesa Diretora a torna incompatível com o exercício da advocacia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 6, 'C', 'A função de Deise como integrante da Mesa Diretora do Parlamento Estadual é conciliável com o exercício da advocacia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 6, 'D', 'A atividade parlamentar de Deise na Mesa Diretora pode ser conciliada com o exercício da advocacia em prol dos necessitados.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 7, 'O advogado F recebe do seu cliente WW determinada soma em dinheiro para aplicação em instrumentos necessários à exploração de jogo não autorizado por lei. Nos termos do Estatuto da Advocacia, a infração disciplinar', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 7, 'A', 'decorre somente se o advogado exige o valor para aplicação ilícita.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 7, 'B', 'surge diante do recebimento para aplicação ilícita.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 7, 'C', 'inocorre, pois se trata de mero ilícito moral.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 7, 'D', 'é descaracterizada por ausência de previsão legal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 8, 'O advogado Nelson, após estabelecer seu escritório em local estratégico nas proximidades dos prédios que abrigam os órgãos judiciários representantes de todas as esferas da Justiça, resolve publicar anúncio em que, além dos seus títulos acadêmicos, expõe a sua vasta experiência profissional, indicando os vários cargos governamentais ocupados, inclusive o de Ministro de prestigiada área social. Nos termos do Código de Ética da Advocacia, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 8, 'A', 'O anúncio está adequado aos termos do Código, pois indica os títulos acadêmicos e a experiência profissional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 8, 'B', 'O anúncio está adequado aos termos do Código, por não conter adjetivações ou referências elogiosas ao profissional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 8, 'C', 'O anúncio colide com as normas do Código, pois a referência a títulos acadêmicos é vedada por indicar a possibilidade de captação de clientela.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 8, 'D', 'O anúncio colide com as normas do Código, que proíbem a referência a cargos públicos capazes de gerar captação de clientela.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 9, 'Gisella é advogada recém-aprovada no Exame de Ordem e herda diversas causas de um colega de classe que resolveu trilhar outros caminhos, deixando numerosos processos para acompanhamento nos Juízos de primeiro grau. Ao acompanhar uma sessão de julgamento na Câmara Cível do Tribunal W, tem necessidade de apresentar, antes de iniciar o julgamento, alegações escritas aos integrantes do órgão julgador, que somente foram completadas no dia da sessão. Aguardando o início dos trabalhos, assim que os julgadores se apresentaram para o julgamento, a jovem advogada dirigiu-se a eles no sentido de entregar as alegações escritas, sendo admoestada quanto à sua presença no interior da sala de julgamento, na parte reservada aos magistrados. Nos termos do Estatuto da Advocacia, o ingresso dos advogados nas salas de sessões', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 9, 'A', 'está restrito ao espaço da plateia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 9, 'B', 'depende de autorização do Presidente da Câmara.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 9, 'C', 'é livre inclusive na parte reservada aos magistrados.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 9, 'D', 'depende de concordância dos julgadores.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 10, 'Laura formou-se em prestigiada Faculdade de Direito, mas sua prática advocatícia foi limitada, o que a impediu de ter experiência maior no trato com os clientes. Realizou seus primeiros processos para amigos e parentes, cobrando módicas quantias referentes a honorários advocatícios. Ao receber a cliente Telma, próspera empresária, e aceitar defender os seus interesses judicialmente, fica em dúvida quanto aos termos de cobrança inicial dos honorários pactuados. Em razão disso, consulta o advogado Luciano, que lhe informa, segundo os termos do Estatuto da Advocacia, que salvo estipulação em contrário,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 10, 'A', 'metade dos honorários é devida no início do serviço.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 10, 'B', 'um quinto dos honorários é devido ao início do processo judicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 10, 'C', 'a integralidade dos honorários é devida até a decisão de primeira instância.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 10, 'D', 'um terço dos honorários é devido no início do serviço.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 11, 'Hans Kelsen, ao abordar o tema da interpretação jurídica no seu livro Teoria Pura do Direito, fala em ato de vontade e ato de conhecimento. Em relação à aplicação do Direito por um órgão jurídico, assinale a afirmativa correta da interpretação.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 11, 'A', 'Prevalece como ato de conhecimento, pois o Direito é atividade científica e, assim, capaz de prover precisão técnica no âmbito de sua aplicação por agentes competentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 11, 'B', 'Predomina como puro ato de conhecimento, em que o agente escolhe, conforme seu arbítrio, qualquer norma que entenda como válida e capaz de regular o caso concreto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 11, 'C', 'A interpretação cognoscitiva combina-se a um ato de vontade em que o órgão aplicador efetua uma escolha entre as possibilidades reveladas por meio da mesma interpretação cognoscitiva.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 11, 'D', 'A interpretação gramatical prevalece como sendo a única capaz de revelar o conhecimento apropriado da mens legis.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 12, '“Mister é não olvidar que a compreensão do direito como ‘fato histórico-cultural’ implica o conhecimento de que estamos perante uma realidade essencialmente dialética, isto é, que não é concebível senão como ‘processus’, cujos elementos ou momentos constitutivos são fato, valor e norma (...)” (Miguel Reale, in Teoria Tridimensional do Direito) Assinale a opção que corretamente explica a natureza da dialética de complementaridade que, segundo Miguel Reale, caracteriza a Teoria Tridimensional do Direito.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 12, 'A', 'A relação entre os polos opostos que são o fato, a norma e o valor, produz uma síntese conclusiva entre tais polos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 12, 'B', 'A implicação dos opostos na medida em que se desoculta e se revela a aparência da contradição, sem que, com esse desocultamento, os termos cessem de ser contrários.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 12, 'C', 'A síntese conclusiva que se estabelece entre diferentes termos, conforme o modelo hegeliano de tese, antítese e síntese.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 12, 'D', 'A estrutura estática que resulta da lógica de subsunção entre os três termos que constituem a experiência jurídica: fato, norma e valor.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 13, 'Pedro, reconhecido advogado na área do direito público, é contratado para produzir um parecer sobre situação que envolve o pacto federativo entre Estados brasileiros. Ao estudar mais detidamente a questão, conclui que, para atingir seu objetivo, é necessário analisar o alcance das chamadas cláusulas pétreas. Com base na ordem constitucional brasileira vigente, assinale, dentre as opções abaixo, a única que expressa uma premissa correta sobre o tema e que pode ser usada pelo referido advogado no desenvolvimento de seu parecer.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 13, 'A', 'As cláusulas pétreas podem ser invocadas para sustentar a existência de normas constitucionais superiores em face de normas constitucionais inferiores, o que possibilita a existência de normas constitucionais inconstitucionais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 13, 'B', 'Norma introduzida por emenda à constituição se integra plenamente ao texto constitucional, não podendo, portanto, ser submetida a controle de constitucionalidade, ainda que sob alegação de violação à cláusula pétrea.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 13, 'C', 'Mudanças propostas por constituinte derivado reformador estão sujeitas ao controle de constitucionalidade, sendo que as normas ali propostas não podem afrontar cláusulas pétreas estabelecidas na Constituição da República.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 13, 'D', 'Os direitos e as garantias individuais considerados como cláusulas pétreas estão localizados exclusivamente nos dispositivos do Art. 5º, de modo que é inconstitucional atribuir essa qualidade (cláusula pétrea) a normas fundadas em outros dispositivos constitucionais.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 14, 'Dois advogados, com grande experiência profissional e com a justa preocupação de se manterem atualizados, concluem que algumas ideias vêm influenciando mais profundamente a percepção dos operadores do direito a respeito da ordem jurídica. Um deles lembra que a Constituição brasileira vem funcionando como verdadeiro “filtro”, de forma a influenciar todas as normas do ordenamento pátrio com os seus valores. O segundo, concordando, adiciona que o crescente reconhecimento da natureza normativo-jurídica dos princípios pelos tribunais, especialmente pelo Supremo Tribunal Federal, tem aproximado as concepções de direito e justiça (buscada no diálogo racional) e oferecido um papel de maior destaque aos magistrados. As posições apresentadas pelos advogados mantêm relação com uma concepção teórico-jurídica que, no Brasil e em outros países, vem sendo denominada de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 14, 'A', 'neoconstitucionalismo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 14, 'B', 'positivismo-normativista.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 14, 'C', 'neopositivismo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 14, 'D', 'jusnaturalismo.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 15, 'Determinado Tribunal de Justiça vem tendo dificuldades para harmonizar os procedimentos de suas câmaras, órgãos fracionários, em relação à análise, em caráter incidental, da inconstitucionalidade de certas normas como pressuposto para o enfrentamento do mérito propriamente dito. A Presidência do referido Tribunal manifestou preocupação com o fato de o procedimento adotado por três dos órgãos fracionários estar conflitando com aquele tido como correto pela ordem constitucional brasileira. Apenas uma das câmaras adotou procedimento referendado pelo sistema jurídico-constitucional brasileiro. Assinale a opção que o apresenta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 15, 'A', 'A 1ª Câmara, ao reformar a decisão de 1º grau em sede recursal, reconheceu, incidentalmente, a inconstitucionalidade da norma que dava suporte ao direito pleiteado, entendendo que, se o sistema jurídico reconhece essa possibilidade ao juízo monocrático, por razões lógicas, deve estendê-la aos órgãos recursais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 15, 'B', 'A 2ª Câmara, ao analisar o recurso interposto, reconheceu, incidentalmente, a inconstitucionalidade da norma que concedia suporte ao direito pleiteado, fundamentando-se em cristalizada jurisprudência do Superior Tribunal de Justiça sobre o tema.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 15, 'C', 'A 3ª Câmara, ao analisar o recurso interposto, reconheceu, incidentalmente, a inconstitucionalidade da norma que concedia suporte ao direito pleiteado, fundamentando-se em pronunciamentos anteriores do Órgão Especial do próprio Tribunal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 15, 'D', 'A 4ª Câmara, embora não tenha declarado a inconstitucionalidade da norma que conferia suporte ao direito pleiteado, solucionou a questão de mérito afastando a aplicação da referida norma, apesar de estarem presentes os seus pressupostos de incidência.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 16, 'Determinado Estado da Federação vivencia sérios problemas de segurança pública, sendo frequentes as fugas dos presos transportados para participar de atos processuais realizados no âmbito do Poder Judiciário. Para remediar essa situação, foi editada uma lei estadual estabelecendo a possibilidade de utilização do sistema de videoconferência no âmbito do Estado. Diante de tal quadro, assinale a afirmativa que se ajusta à ordem constitucional.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 16, 'A', 'A lei estadual é constitucional, pois a matéria se insere na competência local dos Estados-membros, versando sobre assunto de interesse local.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 16, 'B', 'A lei estadual é inconstitucional, pois afrontou a competência privativa da União de legislar sobre Direito Processual Penal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 16, 'C', 'A lei estadual é constitucional, pois a matéria se insere no âmbito da competência delegada da União, versando sobre direito processual.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 16, 'D', 'A lei estadual é inconstitucional, pois comando normativo dessa natureza, por força do princípio da simetria, deveria estar previsto na Constituição Estadual.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 17, 'Um representante da sociedade civil, apresentando indícios de que o Presidente da República teria ultrapassado os gastos autorizados pela lei orçamentária e, portanto, cometido crime de responsabilidade, denuncia o Chefe do Poder Executivo Federal à Câmara dos Deputados. Protocolizada a denúncia na Câmara, foram observados os trâmites legais e regimentais de modo que o Plenário pudesse ou não autorizar a instauração de processo contra o Presidente da República. Do total de 513 deputados da Câmara, apenas 400 estiveram presentes à sessão, sendo que 260 votaram a favor da instauração do processo. Diante desse fato,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 17, 'A', 'o processo será enviado ao Senado Federal para que este, sob a presidência do Presidente do STF, proceda ao julgamento do Presidente da República.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 17, 'B', 'o processo será enviado ao Supremo Tribunal Federal, a fim de que a Corte Maior proceda ao julgamento do Presidente da República.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 17, 'C', 'o processo deverá ser arquivado, tendo em vista o fato de a decisão da Câmara dos Deputados não ter contado com a manifestação favorável de dois terços dos seus membros.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 17, 'D', 'dá-se o impeachment do Presidente da República, que perde o cargo e fica inabilitado para o exercício de outra função pública por oito anos.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 18, 'A discussão a respeito das funções executiva, legislativa e judiciária parece se acirrar em torno dos limites do seu exercício pelos três tradicionais Poderes. Nesse sentido, sobre a estrutura adotada pela Constituição brasileira de 1988, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 18, 'A', 'O exercício da função legislativa é uma atribuição concedida exclusivamente ao Poder Legislativo, como decorrência natural de ser considerado o Poder que mais claramente representa o regime democrático.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 18, 'B', 'O exercício da função jurisdicional é atribuição privativa do Poder Judiciário, embora se possa dizer que o Poder Executivo, no uso do seu poder disciplinar, também faça uso da função jurisdicional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 18, 'C', 'O exercício de funções administrativas, judiciárias e legislativas deve respeitar a mais estrita divisão de funções, não existindo possibilidade de que um Poder venha a exercer, atipicamente, funções afetas a outro Poder.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 18, 'D', 'A produção de efeitos pelas normas elaboradas pelos Poderes Legislativo e Executivo pode ser limitada pela atuação do Poder Judiciário, no âmbito de sua atuação típica de controlar a constitucionalidade ou a legalidade das normas do sistema.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 19, 'Ocorreu um grande escândalo de desvio de verbas públicas na administração pública federal, o que ensejou a instauração de uma Comissão Parlamentar de Inquérito (CPI), requerida pelos deputados federais de oposição. Surpreendentemente, os oponentes da CPI conseguem que o inexperiente deputado M seja alçado à condição de Presidente da Comissão. Por não possuir formação jurídica e desconhecer o trâmite das atividades parlamentares, o referido Presidente, sem consultar os assessores jurídicos da Casa, toma uma série de iniciativas, expedindo ofícios e requisitando informações a diversos órgãos. Posteriormente, veio à tona que apenas uma de suas providências prescindiria de efetivo mandado judicial. Assinale a opção que indica a única providência que o deputado M poderia ter tomado, prescindindo de ordem judicial.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 19, 'A', 'Determinação de prisão preventiva de pessoas por condutas que, embora sem flagrância, configuram crime e há comprovado risco de que voltem a ser praticadas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 19, 'B', 'Autorização, ao setor de inteligência da Polícia Judiciária, para que realize a interceptação das comunicações telefônicas (“escuta”) de prováveis envolvidos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 19, 'C', 'Quebra de sigilo fiscal dos servidores públicos que, sem aparente motivo, apresentaram público e notório aumento do seu padrão de consumo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 19, 'D', 'Busca e apreensão de documentos nas residências de sete pessoas supostamente envolvidas no esquema de desvio de verba.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 20, 'A Comissão Interamericana de Direitos Humanos é competente para examinar comunicações encaminhadas por indivíduos ou grupos de indivíduos que contenham denúncia de violação de direitos previstos na Convenção Americana de Direitos Humanos, violação essa que tenha sido cometida por um Estado-parte. Após receber a denúncia e considerá-la admissível, a Comissão deverá requerer mais informações e buscar uma solução amistosa. Em não ocorrendo tal solução, enviará um informe ao Estado, concedendo-lhe três meses para cumprir suas exigências. Caso o Estado não atenda às exigências deliberadas pela Comissão, esta poderá', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 20, 'A', 'encaminhar o caso para deliberação pela Assembleia Geral da OEA.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 20, 'B', 'proceder ao desligamento do Estado violador da Organização dos Estados Americanos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 20, 'C', 'enviar o caso à Corte Interamericana de Justiça ou à Corte Internacional de Haia, desde que escolha apenas uma das duas Cortes para evitar litispendência no sistema internacional de proteção dos Direitos Humanos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 20, 'D', 'elaborar um segundo informe ao Estado ou enviar o caso à Corte Interamericana de Justiça.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 21, 'A Lei nº 12.986/14 transformou o antigo Conselho de Defesa dos Direitos da Pessoa Humana – CDDPH – em Conselho Nacional dos Direitos Humanos – CNDH. A respeito da finalidade desse Conselho, de acordo com a lei mencionada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 21, 'A', 'Deve apresentar as demandas brasileiras relativas aos direitos humanos junto aos organismos internacionais e multilaterais de proteção dos Direitos Humanos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 21, 'B', 'Deve representar o Estado brasileiro em todas as notificações que este venha a receber em função de procedimentos, como parte da Comissão Interamericana de Direitos Humanos, ou de processos movidos contra o Brasil na Corte Interamericana de Direitos Humanos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 21, 'C', 'Deve elaborar um projeto nacional de Educação para os Direitos Humanos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 21, 'D', 'Deve promover e defender os direitos humanos mediante ações preventivas, protetivas, reparadoras e sancionadoras das condutas e situações de ameaça ou da violação desses direitos.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 22, 'Segundo dados do CONARE (Comitê Nacional para os Refugiados), o Brasil possuía, no fim de 2014, 6.492 refugiados de 80 nacionalidades. Como é sabido, o Brasil ratificou a Convenção das Nações Unidas sobre o Estatuto dos Refugiados, assim como promulgou a Lei nº 9.474/97, que define os mecanismos para a implementação dessa Convenção. Assinale a opção que, conforme a lei mencionada, define a condição jurídica do refugiado no Brasil.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 22, 'A', 'Possui os direitos e deveres dos estrangeiros no Brasil, bem como direito a cédula de identidade comprobatória de sua condição jurídica, carteira de trabalho e documento de viagem.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 22, 'B', 'Está sujeito aos deveres dos estrangeiros no Brasil e tem direito a documento de viagem para deixar o país quando for de sua vontade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 22, 'C', 'Sendo acolhido como refugiado, tem todos os direitos previstos no seu país de origem, mas deve acatar os deveres impostos a todos os brasileiros. Também tem direito à cédula de identidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 22, 'D', 'Possui os direitos e deveres dos estrangeiros no Brasil, bem como direito a cédula de identidade comprobatória de sua condição jurídica, carteira de trabalho, documento de viagem e título de eleitor.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 23, 'Carlos, brasileiro naturalizado, tendo renunciado à sua anterior nacionalidade, casou-se com Tatiana, de nacionalidade alemã. Em razão do trabalho na iniciativa privada, Carlos foi transferido para o Chile, indo residir lá com sua mulher. Em 15/07/2011, em território chileno, nasceu a primeira filha do casal, Cláudia, que foi registrada na Repartição Consular do Brasil. A teor das regras contidas na Constituição Brasileira de 1988, assinale qual a situação de Cláudia quanto à sua nacionalidade.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 23, 'A', 'Cláudia não pode ser considerada brasileira nata, em virtude de a nacionalidade brasileira de seu pai ter sido adquirida de modo derivado e pelo fato de sua mãe ser estrangeira.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 23, 'B', 'Cláudia é brasileira nata, pelo simples fato de o seu pai, brasileiro, ter se mudado por motivo de trabalho.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 23, 'C', 'Cláudia somente será brasileira nata se vier a residir no Brasil e fizer a opção pela nacionalidade brasileira após atingir a maioridade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 23, 'D', 'Cláudia é brasileira nata, não constituindo óbice o fato de o seu pai ser brasileiro naturalizado e sua mãe, estrangeira.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 24, 'A sociedade empresária brasileira do ramo de comunicação, Personalidades, celebrou contrato internacional de prestação de serviços de informática, no Brasil, com a sociedade empresária uruguaia Sacramento. O contrato foi celebrado em Caracas, capital venezuelana, tendo sido estabelecido pelas partes, como foro de eleição, Montevidéu. Diante da situação exposta, à luz das regras do Direito Internacional Privado veiculadas na Lei de Introdução às Normas do Direito Brasileiro (LIND', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 24, 'B', 'e no Código de Processo Civil, assinale a afirmativa correta.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 24, 'A', 'No tocante à regência das obrigações previstas no contrato, aplica-se a legislação uruguaia, já que Montevidéu foi eleito o foro competente para se dirimir eventual controvérsia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 24, 'B', 'Para qualificar e reger as obrigações do presente contrato, aplicar-se-á a lei venezuelana.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 24, 'C', 'Como a execução da obrigação avençada entre as partes se dará no Brasil, aplica-se, obrigatoriamente, no tocante ao cumprimento do contrato, a legislação brasileira.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 25, 'Após ser intimada da lavratura de um auto de infração visando à cobrança da Contribuição para o Financiamento da Seguridade Social (COFINS) dos últimos cinco anos, a pessoa jurídica XYZ Participações Ltda. verificou que o tributo não era devido e ofereceu impugnação ao auto de infração. Como irá participar de uma licitação, a pessoa jurídica em questão irá precisar de certidão de regularidade fiscal – no caso, Certidão Positiva de Débito com Efeito de Negativa (CPD-EN). Na hipótese, considerando que o contribuinte não possui outros débitos, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 25, 'A', 'A impugnação ao auto de infração exclui o crédito tributário, sendo possível a emissão da CPD-EN.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 25, 'B', 'A impugnação ao auto de infração, sem o pagamento do crédito, impede a emissão da CPD-EN.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 25, 'C', 'A pessoa jurídica XYZ Participações Ltda. somente terá direito à CPD-EN caso realize o depósito do montante integral.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 25, 'D', 'A impugnação ao auto de infração suspende a exigibilidade do crédito, sendo possível a emissão da CPD-EN.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 26, 'A União ajuizou execução fiscal em face da pessoa jurídica XYZ Ltda., devedora de tributos federais. No curso da execução fiscal, a falência da pessoa jurídica foi decretada. Após requerimento da União, deferido pelo Juízo, Francisco, sócio da pessoa jurídica XYZ Ltda., é incluído no polo passivo da execução fiscal, em razão da decretação de falência. Sobre a hipótese, é possível afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 26, 'A', 'a decretação de falência autoriza o redirecionamento da execução fiscal para Francisco, por ser considerada hipótese de infração à lei, que enseja responsabilidade tributária.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 26, 'B', 'o fato de Francisco ser sócio da XYZ Ltda. acarreta, por si só, responsabilidade pessoal pelos créditos correspondentes a obrigações tributárias da pessoa jurídica.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 26, 'C', 'Francisco não poderia ser incluído no polo passivo, ainda que fosse administrador da XYZ Ltda. e tivesse encerrado ilegalmente as atividades da pessoa jurídica.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 26, 'D', 'Francisco não poderia, unicamente em razão da decretação de falência de XYZ Ltda., ser incluído no polo passivo da execução fiscal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 27, 'A pessoa jurídica X foi contemplada com isenção do Imposto sobre a Propriedade Predial e Territorial Urbana (IPTU) do imóvel utilizado para suas atividades comerciais. A referida isenção foi concedida pelo prazo de 5 (cinco) anos e sob a condição de que o imóvel seja utilizado para a produção de artesanato regional. Com base no caso apresentado, sobre a referida isenção assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 27, 'A', 'Poderá ser suprimida por lei, a qualquer tempo, ainda que o contribuinte atenda à condição de utilizar o imóvel para a produção de artesanato regional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 27, 'B', 'Não poderá ser suprimida no prazo de 5 (cinco) anos, caso o contribuinte cumpra a condição de utilizar o imóvel para a produção de artesanato regional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 27, 'C', 'Não poderá ser suprimida no prazo de 5 (cinco) anos, ainda que o contribuinte utilize o imóvel para a realização de atividades comerciais diversas da produção de artesanato regional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 27, 'D', 'Poderá ser suprimida por decreto, a qualquer tempo, ainda que o contribuinte cumpra a condição de utilizar o imóvel para a produção de artesanato regional.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 28, 'Em 17/07/2014, o Tribunal de Justiça do Estado X da Federação instituiu, por meio de Provimento da Corregedoria Geral da Justiça, as custas judiciais e os emolumentos cartorários vigentes a partir da data da publicação. Sobre a hipótese, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 28, 'A', 'As custas judiciais e os emolumentos cartorários têm natureza jurídica de preço público e, portanto, não estão sujeitos às limitações constitucionais ao poder de tributar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 28, 'B', 'As custas judiciais e os emolumentos cartorários têm natureza jurídica de taxa de serviço. Sendo assim, o provimento da Corregedoria Geral viola os princípios da legalidade, da anterioridade de exercício e nonagesimal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 28, 'C', 'As custas judiciais e os emolumentos cartorários têm natureza jurídica de contribuição social. Sendo assim, o provimento da Corregedoria Geral viola os princípios da legalidade, da anterioridade de exercício e nonagesimal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 28, 'D', 'As custas judiciais e os emolumentos cartorários têm natureza jurídica de taxa de poder de polícia. Sendo assim, o provimento da Corregedoria Geral viola os princípios da legalidade e da anterioridade de exercício.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 29, 'O Governador do Estado Y criticou, por meio da imprensa, o Diretor-Presidente da Agência Reguladora de Serviços Delegados de Transportes do Estado, autarquia estadual criada pela Lei nº 1.234, alegando que aquela entidade, ao aplicar multas às empresas concessionárias por supostas falhas na prestação do serviço, “não estimula o empresário a investir no Estado”. Ainda, por essa razão, o Governador ameaçou, também pela imprensa, substituir o Diretor- Presidente da agência antes de expirado o prazo do mandato daquele dirigente. Considerando o exposto, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 29, 'A', 'A adoção do mandato fixo para os dirigentes de agências reguladoras contribui para a necessária autonomia da entidade, impedindo a livre exoneração pelo chefe do Poder Executivo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 29, 'B', 'A agência reguladora, como órgão da Administração Direta, submete-se ao poder disciplinar do chefe do Poder Executivo estadual.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 29, 'C', 'A agência reguladora possui personalidade jurídica própria, mas está sujeita, obrigatoriamente, ao poder hierárquico do chefe do Poder Executivo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 29, 'D', 'Ainda que os dirigentes da agência reguladora exerçam mandato fixo, pode o chefe do Poder Executivo exonerá- los, por razões políticas não ligadas ao interesse público, caso discorde das decisões tomadas pela entidade.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 30, 'Fernando, servidor público de uma autarquia federal há nove anos, foi acusado de participar de um esquema para favorecer determinada empresa em uma dispensa de licitação, razão pela qual foi instaurado processo administrativo disciplinar, que resultou na aplicação da penalidade de demissão. Sobre a situação apresentada, considerando que Fernando é ocupante de cargo efetivo, por investidura após prévia aprovação em concurso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 30, 'A', 'Fernando não pode ser demitido do serviço público federal, uma vez que é servidor público estável.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 30, 'B', 'Fernando somente pode ser demitido mediante sentença judicial transitada em julgado, uma vez que a vitaliciedade é garantida aos servidores públicos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 30, 'C', 'É possível a aplicação de penalidade de demissão a Fernando, servidor estável, mediante processo administrativo em que lhe seja assegurada ampla defesa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 30, 'D', 'A aplicação de penalidade de demissão ao servidor público que pratica ato de improbidade independe de processo administrativo ou de sentença judicial.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 31, 'O Município W, durante a construção de avenida importante, ligando a região residencial ao centro comercial da cidade, verifica a necessidade de ampliação da área a ser construída, mediante a incorporação de terrenos contíguos à área já desapropriada, a fim de permitir o prosseguimento das obras. Assim, expede novo decreto de desapropriação, declarando a utilidade pública dos imóveis indicados, adjacentes ao plano da pista. Diante deste caso, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 31, 'A', 'É válida a desapropriação, pelo Município W, de imóveis a serem demolidos para a construção da obra pública, mas não a dos terrenos contíguos à obra.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 31, 'B', 'Não é válida a desapropriação, durante a realização da obra, pelo Município W, de novos imóveis, qualquer que seja a finalidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 31, 'C', 'É válida, no curso da obra, a desapropriação, pelo Município W, de novos imóveis em área contígua necessária ao desenvolvimento da obra.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 31, 'D', 'Em relação às áreas contíguas à obra, a única forma de intervenção estatal da qual pode se valer o Município W é a ocupação temporária.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 32, 'O Estado X publicou edital de concurso público de provas e títulos para o cargo de analista administrativo. O edital prevê a realização de uma primeira fase, com questões objetivas, e de uma segunda fase com questões discursivas, e que os 100 (cem) candidatos mais bem classificados na primeira fase avançariam para a realização da segunda fase. No entanto, após a divulgação dos resultados da primeira fase, é publicado um edital complementar estabelecendo que os 200 (duzentos) candidatos mais bem classificados avançariam à segunda fase e prevendo uma nova forma de composição da pontuação global. Nesse caso,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 32, 'A', 'a alteração não é válida, por ofensa ao princípio da impessoalidade, advindo da adoção de novos critérios de pontuação e da ampliação do número de candidatos na segunda fase.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 32, 'B', 'a alteração é válida, pois a aprovação de mais candidatos na primeira fase não gera prejuízo aos candidatos e ainda permite que mais interessados realizem a prova de segunda fase.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 32, 'C', 'a alteração não é válida, porque o edital de um concurso público não pode conter cláusulas ambíguas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 32, 'D', 'a alteração é válida, pois foi observada a exigência de provimento dos cargos mediante concurso público de provas e títulos.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 33, 'Após autorização em lei, o Estado X constituiu empresa pública para atuação no setor bancário e creditício. Por não possuir, ainda, quadro de pessoal, foi iniciado concurso público com vistas à seleção de 150 empregados, entre economistas, administradores e advogados. A respeito da situação descrita, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 33, 'A', 'Não é possível a constituição de empresa pública para exploração direta de atividade econômica pelo Estado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 33, 'B', 'A lei que autorizou a instituição da empresa pública é, obrigatoriamente, uma lei complementar, por exigência do texto constitucional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 33, 'C', 'Após a Constituição de 1988, cabe às empresas públicas a prestação de serviços públicos e às sociedades de economia mista cabe a exploração de atividade econômica.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 33, 'D', 'A empresa pública que explora atividade econômica sujeita-se ao regime trabalhista próprio das empresas privadas, o que não afasta a exigência de concurso público.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 34, 'Manoel da Silva é comerciante, proprietário de uma padaria e confeitaria de grande movimento na cidade ABCD. A fim de oferecer ao público um serviço diferenciado, Manoel formulou pedido administrativo de autorização de uso de bem público (calçada), para a colocação de mesas e cadeiras. Com a autorização concedida pelo Município, Manoel comprou mobiliário de alto padrão para colocá-lo na calçada, em frente ao seu estabelecimento. Uma semana depois, entretanto, a Prefeitura revogou a autorização, sem apresentar fundamentação. A respeito do ato da prefeitura, que revogou a autorização, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 34, 'A', 'Por se tratar de ato administrativo discricionário, a autorização e sua revogação não podem ser investigadas na via judicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 34, 'B', 'A despeito de se tratar de ato administrativo discricionário, é admissível o controle judicial do ato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 34, 'C', 'A autorização de uso de bem público é ato vinculado, de modo que, uma vez preenchidos os pressupostos, não poderia ser negado ao particular o direito ao seu uso, por meio da revogação do ato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 34, 'D', 'A autorização de uso de bem público é ato discricionário, mas, uma vez deferido o uso ao particular, passa-se a estar diante de ato vinculado, que não admite revogação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 35, 'O Município Z deseja implementar política pública ambiental, no sentido de combater a poluição das vias públicas. Sobre as competências ambientais distribuídas pela Constituição, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 35, 'A', 'União, Estados, Distrito Federal e Municípios têm competência material ambiental comum, devendo leis complementares fixar normas de cooperação entre os entes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 35, 'B', 'Em relação à competência material ambiental, em não sendo exercida pela União e nem pelo Estado,o Município pode exercê-la plenamente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 35, 'C', 'O Município só pode exercer sua competência material ambiental nos limites das normas estaduais sobre o tema.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 35, 'D', 'O Município não tem competência material em direito ambiental, por falta de previsão constitucional, podendo, porém, praticar atos por delegação da União ou do Estado.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 36, 'Determinado Município, por intermédio de lei que contemplou questões como potencial construtivo, zoneamento de bairros e complexos esportivos, reduziu os limites de uma determinada Unidade de Conservação. Considerando o caso hipotético em tela, assinale a opção que se harmoniza com a legislação ambiental.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 36, 'A', 'A lei municipal em questão será considerada válida e eficaz, pois a redução dos limites de uma Unidade de Conservação pode ser feita até mesmo por Decreto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 36, 'B', 'A redução de limites, assim como a desafetação de uma Unidade de Conservação, não demanda lei específica, exigindo apenas a necessária e prévia aprovação de Estudo de Impacto Ambiental e respectivo relatório (EIA-RIMA).');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 36, 'C', 'A redução operada pela lei, para produzir efeitos, dependerá da aprovação do Conselho Gestor da Unidade de Conservação impactada, garantindo-se a participação pública direta no referido procedimento de deliberação e aprovação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 36, 'D', 'A redução dos limites da Unidade de Conservação, conquanto possa evidenciar os efeitos concretos da lei, somente pode ser feita mediante lei específica, regra esta que também se aplica à desafetação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 37, 'Angélica concede a Otávia, pelo prazo de vinte anos, direito real de usufruto sobre imóvel de que é proprietária. O direito real é constituído por meio de escritura pública, que é registrada no competente Cartório do Registro de Imóveis. Cinco anos depois da constituição do usufruto, Otávia falece, deixando como única herdeira sua filha Patrícia. Sobre esse caso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 37, 'A', 'Patrícia herda o direito real de usufruto sobre o imóvel.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 37, 'B', 'Patrícia adquire somente o direito de uso sobre o imóvel.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 37, 'C', 'O direito real de usufruto extingue-se com o falecimento de Otávia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 37, 'D', 'Patrícia deve ingressar em juízo para obter sentença constitutiva do seu direito real de usufruto sobre o imóvel.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 38, 'Gilvan (devedor) contrai empréstimo com Haroldo (credor) para o pagamento com juros do valor do mútuo no montante de R$ 10.000,00. Para facilitar a percepção do crédito, a parte do polo ativo obrigacional ainda facultou, no instrumento contratual firmado, o pagamento do montante no termo avençado ou a entrega do único cavalo da raça manga larga marchador da fazenda, conforme escolha a ser feita pelo devedor. Ante os fatos narrados, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 38, 'A', 'Trata-se de obrigação alternativa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 38, 'B', 'Cuida-se de obrigação de solidariedade em que ambas as prestações são infungíveis.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 38, 'C', 'Acaso o animal morra antes da concentração, extingue-se a obrigação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 38, 'D', 'O contrato é eivado de nulidade, eis que a escolha da prestação cabe ao credor.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 39, 'Flávia vendeu para Quitéria seu apartamento e incluiu, no contrato de compra e venda, cláusula pela qual se reservava o direito de recomprá-lo no prazo máximo de 2 (dois) anos. Antes de expirado o referido prazo, Flávia pretendeu exercer seu direito, mas Quitéria se recusou a receber o preço. Sobre o fato narrado, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 39, 'A', 'A cláusula pela qual Flávia se reservava o direito de recomprar o imóvel é ilícita e abusiva, uma vez que Quitéria, ao se tornar proprietária do bem, passa a ter total e irrestrito poder de disposição sobre ele.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 39, 'B', 'A cláusula pela qual Flávia se reservava o direito de recomprar o imóvel é válida, mas se torna ineficaz diante da justa recusa de Quitéria em receber o preço devido.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 39, 'C', 'A disposição incluída no contrato é uma cláusula de preferência, a impor ao comprador a obrigação de oferecer ao vendedor a coisa, mas somente quando decidir vendê- la.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 39, 'D', 'A disposição incluída no contrato é uma cláusula de retrovenda, entendida como o ajuste por meio do qual o vendedor se reserva o direito de resolver o contrato de compra e venda mediante pagamento do preço recebido e das despesas, recuperando a coisa imóvel.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 40, 'Ester, viúva, tinha duas filhas muito ricas, Marina e Carina. Como as filhas não necessitam de seus bens, Ester deseja beneficiar sua irmã, Ruth, por ocasião de sua morte, destinando-lhe toda a sua herança, bens que vieram de seus pais, também pais de Ruth. Ester o(a) procura como advogado(a), indagando se é possível deixar todos os seus bens para sua irmã. Deseja fazê-lo por meio de testamento público, devidamente lavrado em Cartório de Notas, porque suas filhas estão de acordo com esse seu desejo. Assinale a opção que indica a orientação correta a ser transmitida a Ester.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 40, 'A', 'Em virtude de ter descendentes, Ester não pode dispor de seus bens por testamento.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 40, 'B', 'Ester só pode dispor de 1/3 de seu patrimônio em favor de Ruth, cabendo o restante de sua herança às suas filhas Marina e Carina, dividindo-se igualmente o patrimônio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 40, 'C', 'Ester pode dispor de todo o seu patrimônio em favor de Ruth, já que as filhas estão de acordo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 40, 'D', 'Ester pode dispor de 50% de seu patrimônio em favor de Ruth, cabendo os outros 50% necessariamente às suas filhas, Marina e Carina, na proporção de 25% para cada uma.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 41, 'Mateus é proprietário de um terreno situado em área rural do estado de Minas Gerais. Por meio de escritura pública levada ao cartório do registro de imóveis, Mateus concede, pelo prazo de vinte anos, em favor de Francisco, direito real de superfície sobre o aludido terreno. A escritura prevê que Francisco deverá ali construir um edifício que servirá de escola para a população local. A escritura ainda prevê que, em contrapartida à concessão da superfície, Francisco deverá pagar a Mateus a quantia de R$ 30.000,00 (trinta mil reais). A escritura também prevê que, em caso de alienação do direito de superfície por Francisco, Mateus terá direito a receber quantia equivalente a 3% do valor da transação. Nesse caso, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 41, 'A', 'é nula a concessão de direito de superfície por prazo determinado, haja vista só se admitir, no direito brasileiro, a concessão perpétua.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 41, 'B', 'é nula a cláusula que prevê o pagamento de remuneração em contrapartida à concessão do direito de superfície, haja vista ser a concessão ato essencialmente gratuito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 41, 'C', 'é nula a cláusula que estipula em favor de Mateus o pagamento de determinada quantia em caso de alienação do direito de superfície.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 41, 'D', 'é nula a cláusula que obriga Francisco a construir um edifício no terreno.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 42, 'Carlos Pacheco e Marco Araújo, advogados recém-formados, constituem a sociedade P e A Advogados. Para fornecer e instalar todo o equipamento de informática, a sociedade contrata José Antônio, que, apesar de não realizar essa atividade de forma habitual e profissional, comprometeu-se a adimplir sua obrigação até o dia 20/02/2015, mediante o pagamento do valor de R$ 50.000,00 (cinquenta mil reais) no ato da celebração do contrato. O contrato celebrado é de natureza paritária, não sendo formado por adesão. A cláusula oitava do referido contrato estava assim redigida: “O total inadimplemento deste contrato por qualquer das partes ensejará o pagamento, pelo infrator, do valor de R$ 50.000,00 (cinquenta mil reais)”. Não havia, no contrato, qualquer outra cláusula que se referisse ao inadimplemento ou suas consequências. No dia 20/02/2015, José Antônio telefona para Carlos Pacheco e lhe comunica que não vai cumprir o avençado, pois celebrou com outro escritório de advocacia contrato por valor superior, a lhe render maiores lucros. Sobre os fatos narrados, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 42, 'A', 'Diante da recusa de José Antônio a cumprir o contrato, a sociedade poderá persistir na exigência do cumprimento obrigacional ou, alternativamente, satisfazer-se com a pena convencional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 42, 'B', 'A sociedade pode pleitear o pagamento de indenização superior ao montante fixado na cláusula oitava, desde que prove, em juízo, que as perdas e os danos efetivamente sofridos foram superiores àquele valor.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 42, 'C', 'A sociedade pode exigir o cumprimento da cláusula oitava, classificada como cláusula penal moratória, juntamente com o desempenho da obrigação principal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 42, 'D', 'Para exigir o pagamento do valor fixado na cláusula oitava, a sociedade deverá provar o prejuízo sofrido.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 43, 'Maria, solteira, após a morte de seus pais em acidente automobilístico, propõe demanda por alimentos em face de Pedro, seu parente colateral de segundo grau. Diante dos fatos narrados e considerando as normas de Direito Civil, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 43, 'A', 'Como Pedro é parente colateral de Maria, não tem obrigação de prestar alimentos a esta, ainda que haja necessidade por parte dela.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 43, 'B', 'Pedro só será obrigado a prestar alimentos caso Maria não possua ascendentes nem descendentes, ou, se os possuir, estes não tiverem condições de prestá-los ou complementá-los.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 43, 'C', 'A obrigação de prestar alimentos é solidária entre ascendentes, descendentes e colaterais, em havendo necessidade do alimentando e possibilidade do alimentante.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 43, 'D', 'Pedro não tem obrigação de prestar alimentos, pois não é irmão de Maria.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 44, 'O adolescente N. ficou conhecido no bairro onde mora por praticar roubos e furtos e ter a suposta habilidade de nunca ter sido apreendido. Certa noite, N. saiu com o propósito de praticar novos atos de subtração de coisa alheia. Diante da reação de uma vítima a quem ameaçava, N. disparou sua arma de fogo, levando a vítima a óbito. N. não conseguiu fugir, sendo apreendido por policiais que passavam pelo local, no momento em que praticava o ato infracional. Sobre o caso narrado, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 44, 'A', 'A medida de internação não terá cabimento contra N., uma vez que somente poderá ser aplicada em caso de reincidência no cometimento de infrações graves.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 44, 'B', 'Mesmo estando privado de liberdade, N. poderá entrevistar-se pessoalmente com o representante do Ministério Público, mas não terá direito a peticionar diretamente a este ou a qualquer autoridade que seja.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 44, 'C', 'A medida de internação de N. é cabível por se tratar de ato infracional praticado com ameaça e violência contra pessoa, mesmo que não seja caso de reincidência.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 44, 'D', 'Caso N. seja condenado por sentença ao cumprimento de medida de internação, e somente nesse caso, tornam-se obrigatórias as intimações do seu defensor e dos pais ou responsáveis, mesmo que o adolescente tenha sido intimado pessoalmente.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 45, 'Um conselheiro tutelar, ao passar por um parquinho, observa Ana corrigindo o filho, João, por ele não permitir que os amigos brinquem com o seu patinete. Para tanto, a genitora grita, puxa o cabelo e dá beliscões no infante, na presença das outras crianças e mães, que assistem a tudo assustadas. Assinale a opção que indica o procedimento correto do Conselheiro Tutelar.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 45, 'A', 'Requisitar a Polícia Militar para conduzir Ana à Delegacia de Polícia e, após a atuação policial, dar o caso por encerrado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 45, 'B', 'Não intervir, já que Ana está exercendo o seu poder de correção, decorrência do atributo do poder familiar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 45, 'C', 'Intervir imediatamente, orientando Ana para que não corrija o filho dessa forma, e analisar se não seria recomendável a aplicação de uma das medidas previstas no ECA.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 45, 'D', 'Apenas colher elementos para ingressar em Juízo com uma representação administrativa por descumprimento dos deveres inerentes ao poder familiar.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 46, 'Saulo e Bianca são casados há quinze anos e, há dez, decidiram ingressar no ramo das festas de casamento, produzindo os chamados “bem-casados”, deliciosos doces recheados oferecidos aos convidados ao final da festa. Saulo e Bianca não possuem registro da atividade empresarial desenvolvida, sendo essa a fonte única de renda da família. No mês passado, os noivos Carla e Jair encomendaram ao casal uma centena de “bem-casados” no sabor doce de leite. A encomenda foi entregue conforme contratado, no dia do casamento. Contudo, diversos convidados que ingeriram os quitutes sofreram infecção gastrointestinal, já que o produto estava estragado. A impropriedade do produto para o consumo foi comprovada por perícia técnica. Com base no caso narrado, assinale a alternativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 46, 'A', 'O casal Saulo e Bianca se enquadra no conceito de fornecedor do Código do Consumidor, pois fornecem produtos com habitualidade e onerosidade, sendo que apenas Carla e Jair, na qualidade de consumidores indiretos, poderão pleitear indenização.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 46, 'B', 'Embora a empresa do casal Saulo e Bianca não esteja devidamente registrada na Junta Comercial, pode ser considerada fornecedora à luz do Código do Consumidor, e os convidados do casamento, na qualidade de consumidores por equiparação, poderão pedir indenização diretamente àqueles.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 46, 'C', 'O Código de Defesa do Consumidor é aplicável ao caso, sendo certo que tanto Carla e Jair quanto seus convidados intoxicados são consumidores por equiparação e poderão pedir indenização, porém a inversão do ônus da prova só se aplica em favor de Carla e Jair, contratantes diretos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 46, 'D', 'A atividade desenvolvida pelo casal Saulo e Bianca não está oficialmente registrada na Junta Comercial e, portanto, por ser ente despersonalizado, não se enquadra no conceito legal de fornecedor da lei do consumidor, aplicando-se ao caso as regras atinentes aos vícios redibitórios do Código Civil.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 47, 'Tommy adquiriu determinado veículo junto a um revendedor de automóveis usados. Para tanto, fez o pagamento de 60% do valor do bem e financiou os 40% restantes com garantia de alienação fiduciária, junto ao banco com o qual mantém vínculo de conta-corrente. A negociação transcorreu normalmente e o veículo foi entregue. Ocorre que Tommy, alguns meses depois, achou que a obrigação assumida estava lhe sendo excessivamente onerosa. Procurou então você como advogado(a) a fim de saber se ainda assim seria possível questionar o negócio jurídico realizado e pedir revisão do contrato que Tommy sequer possuía. A esse respeito, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 47, 'A', 'A questão versa sobre alienação fiduciária em garantia que transfere ao credor o domínio resolúvel e a posse indireta do bem alienado, não havendo aplicabilidade do Código de Defesa do Consumidor e, portanto, nem o pedido de revisão na hipótese, haja vista que a questão jurídica está submetida unicamente à leitura da norma geral civil, sem a inversão do ônus da prova.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 47, 'B', 'A questão comporta aplicação do CDC, mas para propor ação revisional, a parte deve ingressar com medida cautelar preparatória de exibição de documentos, sob pena de extinção da medida cognitiva revisional por falta de interesse de agir.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 47, 'C', 'A questão versa sobre alienação fiduciária em garantia, que transfere para o devedor a posse direta do bem, tornando-o depositário, motivo pelo qual a questão jurídica rege-se exclusivamente pelas regras impostas pelo Decreto-lei nº 911, de 1969, que estabelece normas de processo sobre alienação fiduciária.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 47, 'D', 'A questão comporta aplicação do CDC, e a ação revisional pode ser proposta independentemente de medida cautelar preparatória de exibição de documentos, já que o pleito de exibição do contrato poderá ser formulado incidentalmente e nos próprios autos.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 48, 'Paulo, casado no regime de comunhão parcial com Jacobina, é empresário enquadrado como microempreendedor individual (MEI). O varão pretende gravar com hipoteca o imóvel onde está situado seu estabelecimento, que serve exclusivamente aos fins da empresa. De acordo com o Código Civil, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 48, 'A', 'Paulo pode, sem necessidade de outorga conjugal, qualquer que seja o regime de bens, gravar com hipoteca os imóveis que integram o seu estabelecimento.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 48, 'B', 'Paulo não pode, sem a outorga conjugal, gravar com hipoteca os imóveis que integram o seu estabelecimento, salvo no regime de separação de bens.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 48, 'C', 'Paulo, qualquer que seja o regime de bens, depende de outorga conjugal para gravar com hipoteca os imóveis que integram o seu estabelecimento.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 48, 'D', 'Paulo pode, sem necessidade de outorga conjugal, gravar com hipoteca os imóveis que integram o seu estabelecimento, salvo no regime da comunhão universal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 49, 'Assinale a alternativa correta em relação aos conceitos de empresa e empresário no Direito Empresarial.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 49, 'A', 'Empresa é a sociedade com ou sem personalidade jurídica; empresário é o sócio da empresa, pessoa natural ou jurídica com responsabilidade limitada ao valor das quotas integralizadas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 49, 'B', 'Empresa é qualquer atividade econômica destinada à produção de bens; empresário é a pessoa natural que exerce profissionalmente a empresa e tenha receita bruta anual de até R$ 100.000,00 (cem mil reais).');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 49, 'C', 'Empresa é a atividade econômica organizada para a produção e/ou a circulação de bens e de serviços; empresário é o titular da empresa, quem a exerce em caráter profissional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 49, 'D', 'Empresa é a repetição profissional dos atos de comércio ou mercancia; empresário é a pessoa natural ou jurídica que pratica de modo habitual tais atos de comércio.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 50, 'José adquiriu dois refrigeradores a prazo numa das filiais de Comércio de Eletrodomésticos Ltda., tendo efetuado pagamento de entrada no valor de 50% do preço. Foi decretada a falência da vendedora e esta não entregou a mercadoria. Interpelado o administrador judicial, este resolveu não executar o contrato. De acordo com as informações do enunciado e as disposições da Lei nº 11.101/2005 (Lei de Falências e Recuperação de Empresas), assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 50, 'A', 'O comprador poderá pedir ao juiz da falência a reserva do valor de seu crédito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 50, 'B', 'O comprador poderá pedir a restituição em dinheiro do valor pago a título de entrada.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 50, 'C', 'O comprador poderá ajuizar ação em face da massa para o cumprimento compulsório do contrato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 50, 'D', 'O comprador terá seu crédito relativo ao valor pago habilitado como quirografário na falência.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 51, 'Perseu, em 2012, ingressa numa sociedade simples, constituída em 2008, formada por cinco pessoas naturais e com sede na cidade de Primeira Cruz. De acordo com as disposições do Código Civil sobre a sociedade simples, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 51, 'A', 'Perseu é responsável por todas as dívidas sociais anteriores à admissão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 51, 'B', 'Perseu responde apenas pelas dívidas sociais posteriores à admissão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 51, 'C', 'Perseu responde apenas pelas dívidas sociais contraídas no ano anterior à admissão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 51, 'D', 'Perseu não responde pelas dívidas sociais anteriores e posteriores à admissão.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 52, 'Lauro emitiu uma nota promissória com vencimento a dia certo em favor da sociedade empresária W Corretora de Imóveis Ltda. Embora o título esteja assinado pelo emitente, nele não constam a data e o lugar de emissão. Há cláusula de juros remuneratórios, com fixação de taxa anual de 12%. Antes do vencimento, o título recebeu aval em branco prestado por Pedro, irmão de Lauro. Sendo certo que os dados omitidos na nota promissória não foram preenchidos pela sociedade empresária antes da cobrança judicial, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 52, 'A', 'Por se tratar de nota promissória com vencimento a dia certo, é válida a cláusula de juros remuneratórios.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 52, 'B', 'O avalista em branco poderá alegar vício de forma como exceção ao pagamento perante a sociedade empresária.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 52, 'C', 'A ausência do lugar de emissão na nota promissória acarreta sua nulidade, em razão da autonomia das obrigações cambiais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 52, 'D', 'Todos os dados omitidos na nota promissória deveriam ter sido preenchidos pela sociedade empresária até o dia do vencimento. Portanto, a ação de cobrança deverá observar o procedimento da ação monitória.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 53, 'Em ação de alimentos promovida por Yolanda em face de Aurélio, o Juiz determinou que Aurélio deveria arcar, na condição de futuro pai, com os valores devidos à gestante durante a gravidez, destinados a cobrir as despesas adicionais decorrentes da gestação, fixando para tal a quantia “x”. A legislação atinente ao tema dá a Aurélio a possibilidade de defesa. Assinale a opção que indica os termos em que a defesa será exercida.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 53, 'A', 'Além dos alimentos gravídicos, o Juiz designará a data para a realização da audiência, que será considerada o termo a quo para o curso do prazo de cinco dias para a defesa do réu.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 53, 'B', 'O réu deverá ser informado da fixação dos alimentos gravídicos, de modo que o prazo de cinco dias será contado a partir da juntada do mandado de citação devidamente cumprido.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 53, 'C', 'O momento para apresentação da defesa do réu, nesse caso, será a audiência de instrução e julgamento, que terá a data determinada na decisão que fixa os alimentos provisórios.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 53, 'D', 'O prazo de 15 dias para o oferecimento de defesa terá início no dia da juntada do mandado que fixou e determinou o pagamento de alimentos gravídicos.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 54, 'O Banco Financeiro S.A. ajuizou contra Marco Antônio ação de busca e apreensão de veículo, em razão do inadimplemento de contrato de financiamento garantido por cláusula de alienação fiduciária. A primeira tentativa de citação foi infrutífera, uma vez que o réu não mais residia no endereço constante da inicial. O Juízo, então, determinou a indicação de novo endereço para a realização da diligência, por decisão devidamente publicada na imprensa oficial. Considerando que o advogado do autor se manteve inerte por prazo superior a 30 dias, o processo foi julgado extinto, sem resolução do mérito, por abandono. Sabendo da impossibilidade de extinção do processo por abandono sem a prévia intimação pessoal da parte para dar regular andamento ao feito, o advogado do autor interpôs recurso de apelação. Assinale a opção que contém a correta natureza do vício apontado e o pedido adequado à pretensão recursal.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 54, 'A', 'Por se tratar de error in procedendo e a causa não estar madura para julgamento, o pedido recursal deve ser de anulação da sentença.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 54, 'B', 'Trata-se de erro material, que justifica o pedido de integração da sentença pelo Tribunal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 54, 'C', 'Em se tratando de error in judicando, o pedido adequado, no caso sob exame, é de reforma da sentença.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 54, 'D', 'Trata-se de erro de procedimento, que justifica o pedido de julgamento do mérito da lide no estado em que se encontra.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 55, 'Aloísio ajuizou ação de anulação de casamento em face de Júlia. No curso do processo, o juiz designou audiência de instrução e julgamento para colheita dos depoimentos pessoais de Aloísio e Júlia e oitiva das testemunhas. Considerando as regras sobre depoimento pessoal previstas no Código de Processo Civil, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 55, 'A', 'O Código de Processo Civil admite que Júlia se valha da escusa do dever de depor sobre fatos torpes que lhe forem imputados por Aloísio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 55, 'B', 'Aloísio e Júlia, apesar de devidamente intimados, poderão se recusar a depor, sem que seja aplicada a pena de confissão, por ser o depoimento pessoal mero meio de prova.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 55, 'C', 'Aloísio e Júlia deverão responder pessoalmente sobre os fatos articulados, podendo consultar notas breves, desde que objetivem completar os esclarecimentos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 55, 'D', 'O Código de Processo Civil veda expressamente que o juiz, de ofício, determine o comparecimento pessoal de Aloísio e Júlia, a fim de interrogá-los sobre os fatos da causa.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 56, 'Mário foi citado em processo de execução, em virtude do descumprimento de obrigação consubstanciada em nota promissória por ele emitida. Alegando excesso de execução, por ter efetuado o pagamento parcial da dívida, Mário opôs embargos à execução. Sobre esses embargos, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 56, 'A', 'Constituem-se em ação autônoma, razão pela qual serão autuados e distribuídos livremente, em homenagem ao princípio do juiz natural.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 56, 'B', 'São cabíveis tanto nas execuções autônomas quanto no cumprimento de sentença.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 56, 'C', 'Em regra, suspendem a execução.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 56, 'D', 'Seu oferecimento independe de efetivação da penhora, depósito ou caução.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 57, 'Em 21/08/2012, Felipe, empresário do ramo de restaurantes, contratou, por R$ 20 mil, mediante pagamento à vista, os serviços de içamento por guindaste da empresa Júnior e Júnior Ltda., a fim de que uma grande piscina fosse levada à cobertura de seu prédio. No contrato, restou definido que todos os serviços deveriam ser executados até o dia 05/11/2012, vez que Felipe havia programado uma festa de inauguração de seu mais novo empreendimento para 10/11/2012. Em 07/11/2012, sem que os serviços fossem executados, Felipe procura seu advogado, que ajuíza uma ação judicial. Sobre os fatos narrados, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 57, 'A', 'O juiz poderá fixar multa periódica (astreintes) para a efetivação da obrigação de se realizar os serviços de içamento, mesmo que não tenha havido pedido específico do autor nesse sentido.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 57, 'B', 'Fixado o valor da multa periódica por decisão judicial irrecorrida, seu montante não poderá ser alterado por força da preclusão temporal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 57, 'C', 'O montante da multa periódica não poderá ultrapassar o do valor da causa, sob pena de enriquecimento ilícito de Felipe.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 57, 'D', 'Fixadas as astreintes pelo juiz, fica vedada a posterior cominação de multa por litigância de má-fé no mesmo processo, por se tratarem, ambas, de sanções de natureza processual.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 58, 'Luan, servidor público do Estado de Minas Gerais, ajuizou ação contra a Fazenda Pública estadual, requerendo a devolução de verbas indevidamente descontadas em seu contracheque sob a rubrica de “contribuição obrigatória ao plano de saúde”. Na oportunidade, demonstrou que o Tribunal de Justiça de Minas Gerais (TJMG) já havia, em anterior ação individual proposta por Thales, outro servidor público estadual, reconhecido a inconstitucionalidade da lei estadual que previa esse desconto, e requereu, assim, a restituição das verbas não prescritas descontadas a tal título. Devidamente ajuizada junto à 1ª Vara de Feitos Tributários da cidade de Belo Horizonte/MG, e após regular tramitação, o magistrado singular acolheu a tese da ré e julgou improcedente o pedido exordial, tendo tal decisão transitado em julgado em 01/04/2012. Sobre os fatos descritos, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 58, 'A', 'Luan poderá se valer de ação anulatória, tendo em vista a manifesta injustiça da sentença.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 58, 'B', 'Se a inconstitucionalidade da lei estadual tivesse sido reconhecida, na ação proposta por Thales, pelo Supremo Tribunal Federal, Luan poderia ignorar a coisa julgada que lhe foi desfavorável.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 58, 'C', 'Luan poderá se valer de uma reclamação constitucional, tendo em vista o desrespeito, pela sentença, de posição jurisprudencial firmada pelo TJMG.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 58, 'D', 'Luan poderia se valer de uma ação rescisória, desde que, para tanto, demonstrasse que houve violação à lei, sendo- lhe vedado, nessa demanda, a rediscussão de matérias fáticas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 59, 'Reconhecida a prática de um injusto culpável, o juiz realiza o processo de individualização da pena, de acordo com o Art. 68 do Código Penal. Segundo a jurisprudência do Superior Tribunal de Justiça, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 59, 'A', 'A condenação com trânsito em julgado por crime praticado em data posterior ao delito pelo qual o agente está sendo julgado pode funcionar como maus antecedentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 59, 'B', 'Não se mostra possível a compensação da agravante da reincidência com a atenuante da confissão espontânea.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 59, 'C', 'Nada impede que a pena intermediária, na segunda fase do critério trifásico, fique acomodada abaixo do mínimo legal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 59, 'D', 'O aumento da pena na terceira fase no roubo circunstanciado exige fundamentação concreta, sendo insuficiente a simples menção ao número de majorantes.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 60, 'Marcus foi definitivamente condenado pela prática de um crime de roubo simples à pena privativa de liberdade de quatro anos de reclusão e multa de dez dias. Apesar de reincidente, em razão de condenação definitiva pretérita pelo delito de furto, Marcus confessou a prática do delito, razão pela qual sua pena foi fixada no mínimo legal. Após cumprimento de determinado período de sanção penal, pretende o apenado obter o benefício do livramento condicional. Considerando o crime praticado e a hipótese narrada, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 60, 'A', 'Marcus não faz jus ao livramento condicional, pois condenado por crime doloso praticado com violência ou grave ameaça à pessoa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 60, 'B', 'O livramento condicional pode ser concedido pelo juiz da condenação logo quando proferida sentença condenatória.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 60, 'C', 'Não é cabível livramento condicional para Marcus, tendo em vista que é condenado reincidente em crime doloso.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 60, 'D', 'Ainda que praticada falta grave, Marcus não terá o seu prazo de contagem para concessão do livramento condicional interrompido.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 61, 'Paulo pretende adquirir um automóvel por meio de sistema de financiamento junto a uma instituição bancária. Para tanto, dirige-se ao estabelecimento comercial para verificar as condições de financiamento e é informado que, quanto maior a renda bruta familiar, maior a dilação do prazo para pagamento e menores os juros. Decide, então, fazer falsa declaração de parentesco ao preencher a ficha cadastral, a fim de aumentar a renda familiar informada, vindo, assim, a obter o financiamento nas condições pretendidas. Considerando a situação narrada e os crimes contra a fé pública, é correto afirmar que Paulo cometeu o delito de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 61, 'A', 'falsificação material de documento público.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 61, 'B', 'falsidade ideológica.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 61, 'C', 'falsificação material de documento particular.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 61, 'D', 'falsa identidade.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 62, 'Marcondes, necessitando de dinheiro para comparecer a uma festa no bairro em que residia, decide subtrair R$ 1.000,00 do caixa do açougue de propriedade de seu pai. Para isso, aproveita-se da ausência de seu genitor, que, naquele dia, comemorava seu aniversário de 63 anos, para arrombar a porta do estabelecimento e subtrair a quantia em espécie necessária. Analisando a situação fática, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 62, 'A', 'Marcondes não será condenado pela prática de crime, pois é isento de pena, em razão da escusa absolutória.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 62, 'B', 'Marcondes deverá responder pelo crime de furto de coisa comum, por ser herdeiro de seu pai.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 62, 'C', 'Marcondes deverá responder pelo crime de furto qualificado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 62, 'D', 'Marcondes deverá responder pelos crimes de dano e furto simples em concurso formal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 63, 'Durante um assalto a uma instituição bancária, Antônio e Francisco, gerentes do estabelecimento, são feitos reféns. Tendo ciência da condição deles de gerentes e da necessidade de que suas digitais fossem inseridas em determinado sistema para abertura do cofre, os criminosos colocam, à força, o dedo de Antônio no local necessário, abrindo, com isso, o cofre e subtraindo determinada quantia em dinheiro. Além disso, sob a ameaça de morte da esposa de Francisco, exigem que este saia do banco, levando a sacola de dinheiro juntamente com eles, enquanto apontam uma arma de fogo para os policiais que tentavam efetuar a prisão dos agentes. Analisando as condutas de Antônio e Francisco, com base no conceito tripartido de crime, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 63, 'A', 'Antônio não responderá pelo crime por ausência de tipicidade, enquanto Francisco não responderá por ausência de ilicitude em sua conduta.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 63, 'B', 'Antônio não responderá pelo crime por ausência de ilicitude, enquanto Francisco não responderá por ausência de culpabilidade em sua conduta.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 63, 'C', 'Antônio não responderá pelo crime por ausência de tipicidade, enquanto Francisco não responderá por ausência de culpabilidade em sua conduta.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 63, 'D', 'Ambos não responderão pelo crime por ausência de culpabilidade em suas condutas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 64, 'Cristiane, revoltada com a traição de seu marido, Pedro, decide matá-lo. Para tanto, resolve esperar que ele adormeça para, durante a madrugada, acabar com sua vida. Por volta das 22h, Pedro deita para ver futebol na sala da residência do casal. Quando chega à sala, Cristiane percebe que Pedro estava deitado sem se mexer no sofá. Acreditando estar dormindo, desfere 10 facadas em seu peito. Nervosa e arrependida, liga para o hospital e, com a chegada dos médicos, é informada que o marido faleceu. O laudo de exame cadavérico, porém, constatou que Pedro havia falecido momentos antes das facadas em razão de um infarto fulminante. Cristiane, então, foi denunciada por tentativa de homicídio. Você, advogado(a) de Cristiane, deverá alegar em seu favor a ocorrência de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 64, 'A', 'crime impossível por absoluta impropriedade do objeto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 64, 'B', 'desistência voluntária.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 64, 'C', 'arrependimento eficaz.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 64, 'D', 'crime impossível por ineficácia do meio.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 65, 'No dia 01/04/2014, Natália recebeu cinco facadas em seu abdômen, golpes estes que foram a causa eficiente de sua morte. Para investigar a autoria do delito, foi instaurado inquérito policial e foram realizadas diversas diligências, dentre as quais se destacam a oitiva dos familiares e amigos da vítima e exame pericial no local. Mesmo após todas essas medidas, não foi possível obter indícios suficientes de autoria, razão pela qual o inquérito policial foi arquivado pela autoridade judiciária por falta de justa causa, em 06/10/2014, após manifestação nesse sentido da autoridade policial e do Ministério Público. Ocorre que, em 05/01/2015, a mãe de Natália encontrou, entre os bens da filha que ainda guardava, uma carta escrita por Bruno, ex- namorado de Natália, em 30/03/2014, em que ele afirmava que ela teria 24 horas para retomar o relacionamento amoroso ou deveria arcar com as consequências. A referida carta foi encaminhada para a autoridade policial. Nesse caso,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 65, 'A', 'nada poderá ser feito, pois o arquivamento do inquérito policial fez coisa julgada material.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 65, 'B', 'a carta escrita por Bruno pode ser considerada prova nova e justificar o desarquivamento do inquérito pela autoridade competente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 65, 'C', 'nada poderá ser feito, pois a carta escrita antes do arquivamento não pode ser considerada prova nova.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 65, 'D', 'pela falta de justa causa, o arquivamento poderia ter sido determinado diretamente pela autoridade policial, independentemente de manifestação do Ministério Público ou do juiz.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 66, 'Após regular instrução processual, Flávio foi condenado pela prática do crime de tráfico ilícito de entorpecentes a uma pena privativa de liberdade de cinco anos de reclusão, a ser cumprida em regime inicial fechado, e 500 dias-multa. Intimado da sentença, sem assistência da defesa técnica, Flávio renunciou ao direito de recorrer, pois havia confessado a prática delitiva. Rafael, advogado de Flávio, porém, interpôs recurso de apelação dentro do prazo legal, buscando a mudança do regime de pena. Neste caso, é correto dizer que o recurso apresentado por Rafael', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 66, 'A', 'não poderá ser conhecido, pois houve renúncia por parte de Flávio, mas nada impede que o Tribunal, de ofício, melhore a situação do acusado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 66, 'B', 'deverá ser conhecido, pois não é admissível a renúncia ao direito de recorrer, no âmbito do processo penal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 66, 'C', 'não poderá ser conhecido, pois a renúncia expressa de Flávio não pode ser retratada, não podendo o Tribunal, de ofício, alterar a decisão do magistrado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 66, 'D', 'deverá ser conhecido, pois a renúncia foi manifestada sem assistência do defensor.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 67, 'Durante 35 anos, Ricardo exerceu a função de juiz de direito junto ao Tribunal de Justiça de Minas Gerais. Contudo, no ano de 2012, decidiu se aposentar e passou a morar em Florianópolis, Santa Catarina. No dia 22/01/2015, travou uma discussão com seu vizinho e acabou por ser autor de um crime de lesão corporal seguida de morte, consumado na cidade em que reside. Oferecida a denúncia, de acordo com a jurisprudência majoritária dos Tribunais Superiores, será competente para julgar Ricardo', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 67, 'A', 'o Tribunal de Justiça do Estado de Minas Gerais.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 67, 'B', 'uma das Varas Criminais de Florianópolis.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 67, 'C', 'o Tribunal de Justiça de Santa Catarina.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 67, 'D', 'o Tribunal do Júri de Florianópolis.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 68, 'Carlos foi indiciado pela prática de um crime de lesão corporal grave, que teria como vítima Jorge. Após o prazo de 30 dias, a autoridade policial elaborou relatório conclusivo e encaminhou o procedimento para o Ministério Público. O promotor com atribuição concluiu que não existiam indícios de autoria e materialidade, razão pela qual requereu o arquivamento. Inconformado com a manifestação, Jorge contratou advogado e propôs ação penal privada subsidiária da pública. Nesse caso, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 68, 'A', 'caso a queixa seja recebida, o Ministério Público não poderá aditá-la ou interpor recurso no curso do processo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 68, 'B', 'caso a queixa seja recebida, havendo negligência do querelante, deverá ser reconhecida a perempção.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 68, 'C', 'a queixa proposta deve ser rejeitada pelo magistrado, pois não houve inércia do Ministério Público.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 68, 'D', 'a queixa proposta deve ser rejeitada pelo magistrado, tendo em vista que o instituto da ação penal privada subsidiária da pública não foi recepcionado pela Constituição Federal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 69, 'Marcelo foi denunciado pela prática de um crime de furto. Entendendo que não haveria justa causa, antes mesmo de citar o acusado, o magistrado não recebeu a denúncia. Diante disso, o Ministério Público interpôs o recurso adequado. Analisando a hipótese, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 69, 'A', 'o recurso apresentado pelo Ministério Público foi de apelação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 69, 'B', 'apesar de ainda não ter sido citado, Marcelo deve ser intimado para apresentar contrarrazões ao recurso, sob pena de nulidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 69, 'C', 'mantida a decisão do magistrado pelo Tribunal, não poderá o Ministério Público oferecer nova denúncia pelo mesmo fato, ainda que surjam provas novas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 69, 'D', 'antes da rejeição da denúncia, deveria o magistrado ter citado o réu para apresentar resposta à acusação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 70, 'Henrique é técnico de segurança do trabalho da sociedade empresária ALFA e irá aproveitar 20 dias de férias, pois decidiu converter 10 dias de férias em dinheiro. No seu lugar, assumindo de forma plena as tarefas, ficará Vítor, seu melhor assistente e subordinado. Nesse caso, durante o período de férias e de acordo com o entendimento do TST,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 70, 'A', 'Vítor não receberá o mesmo salário, porque a substituição é eventual, por apenas 20 dias.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 70, 'B', 'Vítor terá direito ao mesmo salário de Henrique, pois a substituição não é eventual.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 70, 'C', 'Vítor terá direito ao seu salário e ao de Henrique, porque há acúmulo de funções.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 70, 'D', 'a situação retratada é ilegal, tratando-se de desvio de função, vedado pelo ordenamento jurídico');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 71, 'Josué e Marcos são funcionários da sociedade empresária Empreendimento Seguro Ltda., especializada em consultoria em segurança do trabalho e prevenção de acidentes. No ambiente de trabalho de ambos, também ficam outros 10 funcionários, havendo placas de proibição de fumar, o que era frisado na contratação de cada empregado. O superior hierárquico de todos esses funcionários dividiu as atribuições de cada um, cabendo a Marcos a elaboração da estatística de acidentes ocorridos nos últimos dois anos, tarefa a ser executada em quatro dias. Ao final do prazo, ao entrar na sala, o chefe viu Josué fumando um cigarro. Em seguida, ao questionar Marcos sobre a tarefa, teve como resposta que ele não a tinha executado porque não gostava de fazer estatísticas. Diante do caso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 71, 'A', 'Josué e Marcos são passíveis de ser dispensados por justa causa, respectivamente por atos de indisciplina e insubordinação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 71, 'B', 'Ambos praticaram ato de indisciplina.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 71, 'C', 'Ambos praticaram ato de insubordinação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 71, 'D', 'A conduta de ambos não encontra tipificação legal passível de dispensa por justa causa.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 72, 'Verônica foi contratada, a título de experiência, por 30 dias. Após 22 dias de vigência do contrato, o empregador resolveu romper antecipadamente o contrato, que não possuía cláusula assecuratória do direito recíproco de rescisão. Sobre o caso, de acordo com a Lei de Regência, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 72, 'A', 'O contrato é irregular, pois o contrato de experiência deve ser feito por 90 dias.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 72, 'B', 'Verônica terá direito à remuneração, e por metade, a que teria direito até o termo do contrato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 72, 'C', 'Verônica, como houve ruptura antecipada, terá direito ao aviso prévio e à sua integração ao contrato de trabalho.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 72, 'D', 'O contrato se transformou em contrato por prazo indeterminado, porque ultrapassou metade da sua vigência.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 73, 'Lúcio é enfermeiro num hospital e, após cumprir seu expediente normal de 8 horas de serviço, tratando dos pacientes enfermos, recebe solicitação para prosseguir no trabalho, realizando hora extra. Lúcio se nega, afirmando que a prorrogação não foi autorizada pelo órgão competente do Ministério do Trabalho e do Emprego. Diante desse impasse e de acordo com a CLT, marque a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 73, 'A', 'Lúcio está errado, pois seu dever é de colaboração para com o empregador. A resistência injustificada à sobrejornada dá margem à ruptura por justa causa, por ato de insubordinação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 73, 'B', 'Lúcio está correto, pois é pacífico e sumulado o entendimento de que nenhum empregado é obrigado a realizar horas extras.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 73, 'C', 'Lúcio está errado, pois a legislação em vigor não exige que eventual realização de hora extra seja antecedida de qualquer autorização de órgão governamental.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 73, 'D', 'Lúcio está correto, pois, tratando-se de atividade insalubre, a prorrogação de jornada precisa ser previamente autorizada pela autoridade competente.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 74, 'Jonas é empregado da sociedade empresária Ômega. Entendendo seu empregador por romper seu contrato de trabalho, optou por promover sua imediata demissão, com pagamento do aviso prévio na forma indenizada. Transcorridos 10 dias de pagamento das verbas rescisórias, Jonas se candidatou a dirigente do sindicato da sua categoria e foi eleito presidente na mesma data. Sobre a hipótese apresentada, de acordo com o entendimento consolidado do TST, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 74, 'A', 'Jonas poderá ser desligado ao término do aviso prévio, pois não possui garantia no emprego.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 74, 'B', 'Jonas tem garantia no emprego por determinação legal, porque, pelo fato superveniente, o aviso prévio perde seu efeito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 74, 'C', 'Jonas passou a ser portador de garantia no emprego, não podendo ter o contrato rompido.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 74, 'D', 'Jonas somente poderá ser dispensado se houver concordância do sindicato de classe obreiro.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 75, 'Maria trabalha para a sociedade empresária Alfa S.A. como chefe de departamento. Então, é informada pelo empregador que será transferida de forma definitiva para uma nova unidade da empresa, localizada em outro estado da Federação. Para tanto, Maria, obrigatoriamente, terá de alterar o seu domicílio. Diante da situação retratada e do entendimento consolidado do TST, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 75, 'A', 'Maria receberá adicional de, no mínimo, 25%, mas tal valor, por ter natureza indenizatória, não será integrado ao salário para fim algum.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 75, 'B', 'A empregada não fará jus ao adicional de transferência porque a transferência é definitiva, o que afasta o direito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 75, 'C', 'A obreira terá direito ao adicional de transferência, mas não à ajuda de custo, haja vista o caráter permanente da alteração.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 75, 'D', 'Maria receberá adicional de transferência de 25% do seu salário enquanto permanecer na outra localidade.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 76, 'José é empregado da sociedade empresária Bicicletas Ltda. Necessitando de dinheiro, ele vendeu seu automóvel para seu patrão, sócio da socidade empresária. Para sua surpresa, foi dispensado imotivadamente 4 meses depois. Para garantir o pagamento de horas extras trabalhadas e não pagas, Jonas ajuizou ação trabalhista contra a sociedade empresária Bicicletas Ltda. A defesa da ré aduziu que não devia nenhuma hora extra a Jonas, pois o automóvel vendido ao sócio da ré apresentou defeito no motor, o que gerou prejuízo enorme para ele, razão pela qual tudo deveria ser compensado. Diante disso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 76, 'A', 'Descabe a condenação em horas extras, dado o prejuízo causado, tendo em vista a vedação ao enriquecimento sem causa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 76, 'B', 'Descabe a arguição de compensação de qualquer natureza na Justiça do Trabalho, pois contrária ao princípio de proteção ao hipossuficiente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 76, 'C', 'Descabe a compensação, porque a dívida imputada a José não é trabalhista, devidas assim as horas extras na integralidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 76, 'D', 'Cabe a compensação, desde que arguída em ação própria.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 77, 'Brenda aufere um salário mínimo e meio e ajuizou reclamação trabalhista contra o empregador, postulando diversas verbas que entende fazer jus. Na petição inicial, não houve requerimento de gratuidade de justiça nem declaração de miserabilidade jurídica. O pedido foi julgado improcedente, mas, na sentença, o juiz concedeu, de ofício, a gratuidade de justiça. Diante da situação e do comando legal, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 77, 'A', 'Houve julgamento extra petita no tocante à gratuidade, atraindo a nulidade do julgado, já que isso não foi requerido na petição inicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 77, 'B', 'A Lei é omissa a respeito, daí porque o juiz, invocando o princípio da proteção, poderia conceder espontaneamente a gratuidade de justiça.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 77, 'C', 'A sociedade empresária poderia recorrer para ver reformada a sentença, no tocante à concessão espontânea da gratuidade de justiça, tratando-se de julgamento ultra petita.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 77, 'D', 'O juiz agiu dentro do padrão legal, pois é possível a concessão da gratuidade de justiça de ofício, desde que presentes os requisitos legais, como era o caso.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 78, 'A papelaria Monte Fino Ltda. foi condenada numa reclamação trabalhista movida pelo ex-empregado Sérgio Silva. Uma das parcelas reivindicadas e deferidas foi o 13º salário, que a sociedade empresária insistia haver pago, mas não tinha o recibo em mãos porque houve um assalto na sociedade empresária, quando os bandidos levaram o cofre, as matérias- primas e todos os arquivos com a contabilidade e os documentos da sociedade empresária. Recuperados os arquivos pela polícia, agora, no momento do recurso, a Monte Fino Ltda. pretende juntar o recibo provando o pagamento, inclusive porque a sentença nada mencionou acerca da possível dedução de valores pagos sob o mesmo título. De acordo com o caso apresentado e o entendimento jurisprudencial consolidado, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 78, 'A', 'É possível a juntada do documento no caso concreto, porque provado o justo impedimento para sua oportuna apresentação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 78, 'B', 'O momento de apresentação da prova documental já se esgotou, não sendo possível fazê-lo em sede de recurso.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 78, 'C', 'Pelo princípio da primazia da realidade, qualquer documento pode ser apresentado com sucesso em qualquer grau de jurisdição, inclusive na fase de execução, independentemente de justificativa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 78, 'D', 'Há preclusão, e o juiz não pode aceitar a produção da prova em razão do princípio da proteção, pois isso diminuiria a condenação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 79, 'A sociedade empresária Beta S.A. teve a falência decretada durante a tramitação de uma reclamação trabalhista, fato devidamente informado ao juízo. Depois de julgado procedente em parte o pedido de diferenças de horas extras e de parcelas recisórias, nenhuma das partes recorreu da sentença, que transitou em julgado dessa forma. Teve, então, início a execução, com a apresentação dos cálculos pelo autor e posterior homologação pelo juiz. Diante da situação, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 79, 'A', 'Há equívoco, pois, a partir da decretação da falência, a ação trabalhista passa a ser da competência do juízo falimentar, que deve proferir a sentença.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 79, 'B', 'O pagamento do valor homologado deverá ser feito no juízo da falência, que é universal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 79, 'C', 'A execução será feita diretamente na Justiça do Trabalho, porque o título executivo foi criado pelo Juiz do Trabalho.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 79, 'D', 'Essa é a única hipótese de competência concorrente, ou seja, poderá ser executado tanto na Justiça do Trabalho quanto na Justiça comum.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(17, 80, 'No bojo de uma execução trabalhista, a sociedade empresária executada apresentou uma exceção de pré-executividade, alegando não ter sido citada para a fase de conhecimento. Em razão disso, requereu a nulidade de todo o processo, desde a citação inicial. O juiz conferiu vista à parte contrária para manifestação e, em seguida, determinou a conclusão dos autos. Após analisar as razões da parte e as provas produzidas, convenceu-se de que a alegação da sociedade empresária era correta e, assim, anulou todo o feito desde o início. Diante desse quadro, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 80, 'A', 'Contra essa decisão caberá agravo de petição.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 80, 'B', 'Trata-se de decisão interlocutória e, portanto, não passível de recurso imediato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 80, 'C', 'Caberá a interposição de recurso ordinário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(17, 80, 'D', 'Caberá a interposição de agravo de instrumento.');
commit;
insert into exames (num_exame, url_exame, url_gabarito, data)
values(18, 'https://dpmzos25m8ivg.cloudfront.net/617/29112015184841_CADERNO_TIPO_1_XVIII_EXAME.pdf', 'https://dpmzos25m8ivg.cloudfront.net/617/29112015184750_GABARITOS%20PRELIMIARES_XVIII_EXAME_DE_ORDEM.pdf', 'Sun Nov 29 00:00:00 BRST 2015');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 1, 'Paulo é contratado por Pedro para promover ação com pedido condenatório em face de Alexandre, por danos causados ao animal de sua propriedade. Em decorrência do processo, houve condenação do réu ao pagamento de indenização ao autor, fixados honorários de sucumbência correspondentes a dez por cento do apurado em cumprimento de sentença. O réu ofertou apelação contra a sentença proferida na fase cognitiva. Ainda pendente o julgamento do recurso, Pedro decide revogar o mandato judicial conferido a Paulo, desobrigando-se de pagar os honorários contratualmente ajustados. Nos termos do Código de Ética da OAB, a revogação do mandato judicial, por vontade de Pedro,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 1, 'A', 'não o desobriga do pagamento das verbas honorárias contratadas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 1, 'B', 'desobriga-o do pagamento das verbas honorárias contratadas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 1, 'C', 'desobriga-o do pagamento das verbas honorárias contratadas e da verba sucumbencial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 1, 'D', 'não o desobriga do pagamento das verbas honorárias sucumbenciais, mas o desobriga das verbas contratadas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 2, 'Os advogados criminalistas X e Y atuavam em diversas ações penais e inquéritos em favor de um grupo de pessoas acusadas de pertencer a determinada organização criminosa, supostamente destinada ao tráfico de drogas. Ao perceber que não havia outros meios disponíveis para a obtenção de provas contra os investigados, o juiz, no âmbito de um dos inquéritos instaurados para investigar o grupo, atendendo à representação da autoridade policial e considerando manifestação favorável do Ministério Público, determinou o afastamento do sigilo telefônico dos advogados constituídos nos autos dos aludidos procedimentos, embora não houvesse indícios da prática de crimes por estes últimos. As conversas entre os investigados e seus advogados, bem como aquelas havidas entre os advogados X e Y, foram posteriormente usadas para fundamentar a denúncia oferecida contra seus clientes. Considerando-se a hipótese apresentada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 2, 'A', 'A prova é lícita, pois não havia outro meio disponível para a obtenção de provas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 2, 'B', 'A prova é lícita, pois tratava-se de investigação de prática de crime cometido no âmbito de organização criminosa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 2, 'C', 'Considerando que não havia outro meio disponível para a obtenção de provas, bem como que se tratava de investigação de prática de crime cometido no âmbito de organização criminosa, é ilícita a prova obtida a partir dos diálogos havidos entre os advogados e seus clientes. É, no entanto, lícita a prova obtida a partir dos diálogos havidos entre os advogados X e Y.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 2, 'D', 'A prova é ilícita, uma vez que as comunicações telefônicas do advogado são invioláveis quando disserem respeito ao exercício da profissão, bem como se não houver indícios da prática de crime pelo advogado.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 3, 'A advogada Ana retirou de cartório os autos de determinado processo de conhecimento em que representava a parte ré, para apresentar contestação. Protocolou a petição tempestivamente, mas deixou de devolver os autos em seguida por esquecimento, só o fazendo após ficar pouco mais de um mês com os autos em seu poder. Ao perceber que Ana não devolvera os autos imediatamente após cumprir o prazo, o magistrado exarou despacho pelo qual a advogada foi proibida de retirar novamente os autos do cartório em carga, até o final do processo. Nos termos do Estatuto da Advocacia, deve-se assentar quanto à sanção disciplinar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 3, 'A', 'não se aplica porque Ana não chegou a ser intimada a devolver os autos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 3, 'B', 'não se aplica porque Ana ficou menos de três meses com os autos em seu poder.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 3, 'C', 'aplica-se porque Ana reteve abusivamente os autos em seu poder.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 3, 'D', 'aplica-se porque Ana não poderia ter retirado os autos de cartório para cumprir o prazo assinalado para contestação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 4, 'Fernanda, estudante do 8º período de Direito, requereu inscrição junto à Seccional da OAB do estado onde reside. A inscrição foi indeferida, em razão de Fernanda ser serventuária do Tribunal de Justiça do estado. Fernanda recorreu da decisão, alegando que preenche todos os requisitos exigidos em lei para a inscrição de estagiário e que o exercício de cargo incompatível com a advocacia não impede a inscrição do estudante de Direito como estagiário. Merece ser revista a decisão que indeferiu a inscrição de estagiário de Fernanda?', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 4, 'A', 'Sim, pois Fernanda exerce cargo incompatível com a advocacia e não com a realização de estágio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 4, 'B', 'Não, pois as incompatibilidades previstas em lei para o exercício da advocacia também devem ser observadas quando do requerimento de inscrição de estagiário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 4, 'C', 'Sim, pois o cargo de serventuário do Tribunal de Justiça não é incompatível com a advocacia, menos ainda com a realização de estágio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 4, 'D', 'Não, pois apenas estudantes do último período do curso de Direito podem requerer inscrição como estagiários.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 5, 'Determinada causa em que se discutia a guarda de dois menores estava confiada ao advogado Álvaro, que trabalhava sozinho em seu escritório. Aproveitando o período de recesso forense e considerando que não teria prazos a cumprir ou atos processuais designados durante esse período, Álvaro realizou viagem para visitar a família no interior do estado. Alguns dias depois de sua partida, ainda durante o período de recesso, instalou-se situação que demandaria a tomada de medidas urgentes no âmbito da mencionada ação de guarda. O cliente de Álvaro, considerando que seu advogado se encontrava fora da cidade, procurou outro advogado, Paulo, para que a medida judicial necessária fosse tomada, recorrendo-se ao plantão judiciário. Paulo não conseguiu falar com Álvaro para avisar que atuaria na causa em que este último estava constituído, mas aceitou procuração do cliente assim mesmo e tomou a providência cabível. Poderia Paulo ter atuado na causa sem o conhecimento e a anuência de Álvaro?', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 5, 'A', 'Paulo poderia ter atuado naquela causa apenas para tomar a medida urgente cabível.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 5, 'B', 'Paulo poderia ter atuado na causa, ainda que não houvesse providência urgente a tomar, uma vez que o advogado constituído estava viajando.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 5, 'C', 'Paulo não poderia ter atuado na causa, pois o advogado não pode aceitar procuração de quem já tenha patrono constituído, sem prévio conhecimento deste, ainda que haja necessidade da tomada de medidas urgentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 5, 'D', 'Paulo não poderia ter atuado na causa, pois os prazos estavam suspensos durante o recesso.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 6, 'O Presidente de determinada Seccional da OAB recebeu representação contra advogado que nela era inscrito por meio de missiva anônima, que narrava grave infração disciplinar. Considerando a via eleita para a apresentação da representação, foi determinado o arquivamento do expediente, sem instauração de processo disciplinar. Pouco tempo depois, foi publicada matéria jornalística sobre investigação realizada pela Polícia Federal que tinha como objeto a mesma infração disciplinar que havia sido narrada na missiva anônima e indicando o nome do investigado naquele procedimento inquisitorial. Com base na reportagem, foi determinada, pelo Presidente da Seccional, a instauração de processo disciplinar. Sobre o procedimento adotado pelo Presidente da Seccional em questão, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 6, 'A', 'Deveria ter instaurado processo disciplinar quando recebeu a missiva anônima.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 6, 'B', 'Não poderia ter instaurado processo disciplinar em nenhuma das oportunidades.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 6, 'C', 'Deveria ter instaurado processo disciplinar em qualquer uma das oportunidades.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 6, 'D', 'Poderia ter instaurado processo disciplinar a partir da publicação da matéria jornalística.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 7, 'Os advogados Márcio, Bruno e Jorge, inscritos nas Seccionais do Paraná e de Santa Catarina da Ordem dos Advogados resolveram constituir determinada sociedade civil de advogados, para atuação na área tributária. A sede da sociedade estava localizada em Curitiba. Como os três sócios estavam inscritos na Seccional de Santa Catarina, eles requereram o registro da sociedade também nessa Seccional. Márcio, por outro lado, já fazendo parte da sociedade com Bruno e Jorge, requereu, juntamente com seu irmão, igualmente advogado, o registro de outra sociedade de advogados também na Seccional do Paraná, esta com especialização na área tributária. As sociedades não são filiais. Sobre a hipótese descrita é correto afirmar que a sociedade de advogados de Márcio, Bruno e Jorge', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 7, 'A', 'não poderá ser registrada na seccional de Santa Catarina, pois apenas tem sede na Seccional do Paraná. Márcio não poderá requerer inscrição em outra sociedade de advogados no Paraná.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 7, 'B', 'não poderá ser registrada na seccional de Santa Catarina, pois apenas tem sede na Seccional do Paraná. Márcio poderá requerer inscrição em outra sociedade de advogados no Paraná.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 7, 'C', 'poderá ser registrada na seccional de Santa Catarina, pois os três advogados que dela fazem parte estão inscritos na Seccional em questão. Márcio não poderá requerer inscrição em outra sociedade de advogados no Paraná.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 7, 'D', 'poderá ser registrada na seccional de Santa Catarina, pois os três advogados que dela fazem parte estão inscritos na Seccional em questão. Márcio poderá requerer inscrição em outra sociedade de advogados no Paraná.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 8, 'Gabriela é sócia de uma sociedade de advogados, tendo, no exercício de suas atividades profissionais, representado judicialmente Júlia. Entretanto, Gabriela, agindo com culpa, deixou de praticar ato imprescindível à defesa de Júlia em processo judicial, acarretando-lhe danos materiais e morais. Em uma eventual demanda proposta por Júlia, a fim de ver ressarcidos os danos sofridos, deve-se considerar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 8, 'A', 'Gabriela e a sociedade de advogados não podem ser responsabilizadas civilmente pelos danos, pois, no exercício profissional, o advogado apenas responde pelos atos que pratica mediante dolo, compreendido por meio do binômio consciência e vontade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 8, 'B', 'a sociedade de advogados não pode ser responsabilizada civilmente pelos atos ou omissões praticados pessoalmente por Gabriela. Assim, apenas a advogada responderá pela sua omissão decorrente de culpa, no âmbito da responsabilidade civil e disciplinar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 8, 'C', 'Gabriela e a sociedade de advogados responderão civilmente pela omissão decorrente de culpa, sem prejuízo da responsabilidade disciplinar da advogada, cuidando-se de hipótese de responsabilidade civil solidária entre ambas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 8, 'D', 'Gabriela e a sociedade de advogados podem ser responsabilizadas civilmente pela omissão decorrente de culpa. A responsabilidade civil de Gabriela será subsidiária à da sociedade e ilimitada pelos danos causados, sem prejuízo de sua responsabilidade disciplinar.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 9, 'O banco Dólar é réu em diversos processos de natureza consumerista, todos com idênticos fundamentos de Direito, pulverizados pelo território nacional. Considerando a grande quantidade de feitos e sua abrangência territorial, a instituição financeira decidiu contratar a sociedade de advogados X para sua defesa em juízo, pois esta possui filial em diversos estados da Federação. Diante da consulta formulada pelo banco, alguns advogados, sócios integrantes da filial situada no Rio Grande do Sul, realizaram mapeamento dos processos em trâmite em face da pessoa jurídica. Assim, observaram que esta mesma filial já atua em um dos processos em favor do autor da demanda. Tendo em vista tal situação, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 9, 'A', 'Os advogados deverão recusar, por meio de qualquer sócio do escritório ou filial, a atuação da sociedade de advogados na defesa do banco, pois os advogados sócios de uma mesma sociedade profissional não podem representar em juízo clientes de interesses opostos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 9, 'B', 'Os advogados deverão identificar quem são os sócios do escritório que atuam na causa, pois estes não poderão realizar a defesa técnica do banco em quaisquer dos processos em trâmite, sendo autorizada, porém, a atuação dos demais sócios da sociedade de advogados, de qualquer filial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 9, 'C', 'Os advogados deverão recusar a defesa do banco pela filial da sociedade de advogados no Rio Grande do Sul e indicar as outras filiais para atuação nos feitos, pois todos os sócios da filial ficam impedidos de representar em juízo a instituição financeira, em razão de já haver atuação em favor de cliente com interesses opostos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 9, 'D', 'Os advogados deverão informar ao banco que há atuação de advogados daquela filial em um dos processos em favor do autor da demanda, a fim de que a instituição financeira decida se deseja, efetivamente, que a sua defesa técnica seja realizada pela sociedade de advogados, garantindo, assim, o consentimento informado do cliente.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 10, 'Alice, advogada, em audiência judicial, dirigiu a palavra de maneira ríspida a certa testemunha e ao magistrado, tendo este entendido que houve a prática dos crimes de injúria e desacato, respectivamente. Por isso, o juiz determinou a extração de cópias da ata e remessa à Promotoria de Justiça com atribuição para investigação penal da comarca. Considerando a situação narrada, a disciplina do Estatuto da OAB e o entendimento do Supremo Tribunal Federal, sobre as manifestações de Alice, proferidas no exercício de sua atividade profissional, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 10, 'A', 'podem configurar injúria e desacato puníveis, pois o Supremo Tribunal Federal declarou inconstitucional a imunidade profissional prevista no Art. 7º, § 2º, do Estatuto da OAB, já que a Constituição Federal consagra a incolumidade da honra e imagem.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 10, 'B', 'não podem constituir injúria ou desacato puníveis. Isso porque o advogado tem imunidade profissional, nos termos do Art. 7º, § 2º, do Estatuto da OAB, cuja integral constitucionalidade foi declarada pelo Supremo Tribunal Federal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 10, 'C', 'não podem constituir injúria, mas podem configurar desacato punível. Isso porque o advogado tem imunidade profissional, nos termos do Art. 7º, § 2º, do Estatuto da OAB, mas esta, de acordo com o Supremo Tribunal Federal, não compreende o desacato, sob pena de conflitar com a autoridade do magistrado na condução da atividade jurisdicional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 10, 'D', 'não podem constituir injúria ou desacato puníveis, mas podem caracterizar crime de desobediência. Isso porque o advogado tem imunidade profissional, nos termos do Art. 7º, § 2º, do Estatuto da OAB, cuja constitucionalidade foi declarada pelo Supremo Tribunal Federal, com a ressalva ao delito de desobediência, a fim de não conflitar com a autoridade do magistrado na condução da atividade jurisdicional.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 11, '“A solução do conflito aparente de normas dá-se, na hipótese, mediante a incidência do critério da especialidade, segundo o qual prevalece a norma específica sobre a geral.” É conhecida a distinção no âmbito da Teoria do Direito entre antinomias aparentes (ou antinomias solúveis) e antinomias reais (ou antinomias insolúveis). Para o jusfilósofo Norberto Bobbio, uma antinomia real se caracteriza quando estamos diante', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 11, 'A', 'de duas normas colidentes que pertencem a ordenamentos jurídicos diferentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 11, 'B', 'de normas que colidem entre si, porém essa colisão é solúvel mediante a aplicação do critério cronológico, do critério hierárquico ou do critério de especialidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 11, 'C', 'de normas colidentes e o intérprete é abandonado a si mesmo pela falta de um critério ou pela impossibilidade de solução do conflito entre os critérios existentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 11, 'D', 'de duas ou mais normas que colidem entre si e que possuem diferentes âmbitos de validade temporal, espacial, pessoal ou material.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 12, 'Segundo o jusfilósofo alemão Karl Larenz, os textos jurídicos são problematizáveis porque estão redigidos em linguagem corrente ou em linguagem especializada, mas que, de todo modo, contêm expressões que apresentam uma margem de variabilidade de significação. Nesse sentido, assinale a opção que exprime o pensamento desse autor acerca da ideia de interpretação da lei.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 12, 'A', 'Deve-se aceitar que os textos jurídicos apenas carecem de interpretação quando surgem particularmente como obscuros, pouco claros ou contraditórios.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 12, 'B', 'Interpretar um texto significa alcançar o único sentido possível de uma norma conforme a intenção que a ela foi dada pelo legislador.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 12, 'C', 'Os textos jurídicos, em princípio, são suscetíveis e carecem de interpretação porque toda linguagem é passível de adequação a cada situação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 12, 'D', 'A interpretação dada por uma autoridade judicial a uma lei é uma conclusão logicamente vinculante que, por isso mesmo, deve ser repetida sempre que a mesma lei for aplicada.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 13, 'A Lei Z, elaborada recentemente pelo Poder Legislativo do Município M, foi promulgada e passou a produzir seus efeitos regulares após a Câmara Municipal ter derrubado o veto aposto pelo Prefeito. A peculiaridade é que o conteúdo da lei é praticamente idêntico ao de outras leis que foram editadas em milhares de outros Municípios, o que lhe atribui inegável relevância. Inconformado com a derrubada do veto, o Prefeito do Município M, partindo da premissa de que a Lei Z possui diversas normas violadoras da ordem constitucional federal, pretende que sua inconstitucionalidade seja submetida à apreciação do Supremo Tribunal Federal. A partir das informações acima, assinale a opção que se encontra em consonância com o sistema de controle de constitucionalidade adotado no Brasil.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 13, 'A', 'O Prefeito do Município M, como agente legitimado pela Constituição Federal, está habilitado a propor arguição de descumprimento de preceito fundamental questionando a constitucionalidade dos dispositivos que entende violadores da ordem constitucional federal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 13, 'B', 'A temática pode ser objeto de ação direta de inconstitucionalidade ou de arguição de descumprimento de preceito fundamental, se proposta por qualquer um dos legitimados pelo Art. 103 da Constituição Federal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 13, 'C', 'A Lei Z não poderá ser objeto de ação, pela via concentrada, perante o Supremo Tribunal Federal, já que, de acordo com o sistema de controle de constitucionalidade adotado no Brasil, atos normativos municipais só podem ser objeto de controle, caso se utilize como paradigma de confronto a Constituição Federal, pela via difusa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 13, 'D', 'Os dispositivos normativos da Lei Z, sem desconsiderar a possibilidade de ser realizado o controle incidental pela via difusa, podem ser objeto de controle por via de arguição de descumprimento de preceito fundamental, se proposta por qualquer um dos legitimados pelo Art. 103 da Constituição Federal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 14, 'Muitos Estados ocidentais, a partir do processo revolucionário franco-americano do final do século XVIII, atribuíram aos juízes a função de interpretar a Constituição, daí surgindo a denominada jurisdição constitucional. A respeito do controle de constitucionalidade exercido por esse tipo de estrutura orgânica, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 14, 'A', 'A supremacia da Constituição e a hierarquia das fontes normativas destacam-se entre os pressupostos do controle de constitucionalidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 14, 'B', 'A denominada mutação constitucional é uma modalidade de controle de constitucionalidade realizado pela jurisdição constitucional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 14, 'C', 'O controle concentrado de constitucionalidade consiste na análise da compatibilidade de qualquer norma infraconstitucional com a Constituição.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 14, 'D', 'O controle de constitucionalidade de qualquer decreto regulamentar deve ser realizado pela via difusa.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 15, 'Ao proferir um discurso em sua cidade natal, José, deputado federal pelo Estado E, afirma, de forma contundente, que um país democrático tem por regra inviolável escolher o chefe do Poder Executivo por meio de eleições diretas. Complementa sua fala afirmando que o Brasil poderia ser considerado um país democrático, já que a Constituição Cidadã de 1988 não prevê eleição de Presidente pela via indireta. Segundo a Constituição da República, o deputado está', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 15, 'A', 'equivocado, pois há previsão de eleição indireta somente na eventualidade de vacância do cargo de Presidente da República nos últimos seis meses do seu mandato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 15, 'B', 'correto, pois, sendo o voto direto cláusula pétrea prevista na Constituição, não pode haver situação constitucional que possibilite o uso do voto indireto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 15, 'C', 'equivocado, pois há previsão de eleição indireta no caso de vacância dos cargos de Presidente e Vice-presidente da República nos últimos dois anos do mandato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 15, 'D', 'correto, pois não há previsão de eleição indireta em caso de vacância, já que o cargo de Presidente da República viria a ser ocupado pelo Presidente da Câmara dos Deputados.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 16, 'Luiz é proprietário de uma grande fazenda localizada na zona rural do Estado X. Lá, cultiva café de excelente qualidade – e com grande produtividade – para fins de exportação. Porém, uma fiscalização realizada por agentes do Ministério do Trabalho e do Emprego constatou a exploração de mão de obra escrava. Independentemente das sanções previstas em lei, caso tal prática seja devidamente comprovada, de forma definitiva, pelos órgãos jurisdicionais competentes, a Constituição Federal dispõe que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 16, 'A', 'a propriedade deve ser objeto de desapropriação, respeitado o direito à justa e prévia indenização a que faz jus o proprietário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 16, 'B', 'a propriedade deve ser objeto de expropriação, sem qualquer indenização, e, no caso em tela, destinada à reforma agrária.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 16, 'C', 'o direito de propriedade de Luiz deve ser respeitado, tendo em vista serem as terras em comento produtivas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 16, 'D', 'o direito da propriedade de Luiz deve ser respeitado, pois a expropriação é instituto cabível somente nos casos de cultura ilegal de plantas psicotrópicas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 17, 'Um grupo autodenominado “Sangue Puro” passou a se organizar sob a forma de associação. No seu estatuto, é possível identificar claros propósitos de incitação à violência contra indivíduos pertencentes a determinadas minorias sociais. Diversas organizações não governamentais voltadas à defesa dos direitos humanos, bem como o Ministério Público, ajuizaram medidas judiciais solicitando a sua imediata dissolução. Segundo a Constituição Federal, a respeito da hipótese formulada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 17, 'A', 'A associação não poderá sofrer qualquer intervenção do Poder Judiciário, pois é vedada a interferência estatal no funcionamento das associações.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 17, 'B', 'Caso o pedido de dissolução seja acolhido, a associação poderá ser compulsoriamente dissolvida, independentemente do trânsito em julgado da sentença judicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 17, 'C', 'A associação poderá ter suas atividades imediatamente suspensas por decisão judicial, independentemente do seu trânsito em julgado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 17, 'D', 'Apenas se justificaria a intervenção estatal se caracterizada a natureza paramilitar da associação em comento.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 18, 'A parte da população do Estado V situada ao sul do seu território, insatisfeita com a pouca atenção que vem recebendo dos últimos governos, organiza-se e dá início a uma campanha para promover a criação de um novo Estado- membro da República Federativa do Brasil – o Estado N, que passaria a ocupar o território situado na parte sul do Estado V. O tema desperta muita discussão em todo o Estado, sendo que alguns argumentos favoráveis e outros contrários ao desmembramento começam a ganhar publicidade na mídia. Reconhecido constitucionalista analisa os argumentos listados a seguir e afirma que apenas um deles pode ser referendado pelo sistema jurídico-constitucional brasileiro. Assinale-o.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 18, 'A', 'O desmembramento não poderia ocorrer, pois uma das características fundamentais do Estado Federal é a impossibilidade de ocorrência do chamado direito de secessão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 18, 'B', 'O desmembramento poderá ocorrer, contanto que haja aprovação, por via plebiscitária, exclusivamente por parte da população que atualmente habita o território que formaria o Estado N.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 18, 'C', 'Além de aprovação pela população interessada, o desmembramento também pressupõe a edição de lei complementar pelo Congresso Nacional com esse objeto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 18, 'D', 'Além de manifestação da população interessada, o sistema constitucional brasileiro exige que o desmembramento dos Estados seja precedido de divulgação de estudos de viabilidade.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 19, 'A Assembleia Legislativa do Estado M, ao constatar a ausência de normas gerais sobre matéria em que a União, os Estados e o Distrito Federal possuem competência legislativa concorrente, resolve tomar providências no sentido de legislar sobre o tema, preenchendo os vazios normativos decorrentes dessa lacuna. Assim, dois anos após a Lei E/2013 ter sido promulgada pelo Estado M, o Congresso Nacional promulga a Lei F/2015, estabelecendo normas gerais sobre a matéria. Sobre esse caso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 19, 'A', 'A Lei E/2013 foi devidamente revogada pela Lei F/2015, posto não ser admissível, no caso, que norma estadual pudesse preservar a sua eficácia diante da promulgação de norma federal a respeito da mesma temática.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 19, 'B', 'A Lei E/2013 perde a sua eficácia somente naquilo que contrariar as normas gerais introduzidas pela Lei F/2015, mantendo eficácia a parte que, compatível com a Lei F/2015, seja suplementar a ela.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 19, 'C', 'A Lei F/2015 não poderá viger no território do Estado M, já que a edição anterior da Lei E/2013, veiculando normas específicas, afasta a eficácia das normas gerais editadas pela União em momento posterior.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 19, 'D', 'A competência legislativa concorrente, por ser uma espécie de competência comum entre todos os entes federativos, pode ser usada indistintamente por qualquer deles, prevalecendo, no caso de conflito, a lei posterior, editada pelo Estado ou pela União.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 20, 'Em relação ao direito de liberdade de pensamento e expressão, a Convenção Americana sobre os Direitos Humanos, devidamente ratificada pelo Estado brasileiro, adotou o seguinte posicionamento:', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 20, 'A', 'vedou a censura prévia, mas admite que a lei o faça em relação aos espetáculos públicos apenas como forma de regular o acesso a eles, tendo em vista a proteção moral da infância e da adolescência.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 20, 'B', 'vedou a censura prévia em geral, mas admite que ela ocorra expressamente nos casos de propaganda política eleitoral, tendo em vista a proteção da ordem pública e da segurança nacional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 20, 'C', 'admitiu a censura prévia em geral, tendo em vista a proteção da saúde e da moral públicas, mas a veda expressamente nos casos de propaganda eleitoral, a fim de assegurar a livre manifestação das ideias políticas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 20, 'D', 'admitiu a censura prévia como forma de assegurar o respeito aos direitos e à reputação das demais pessoas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 21, 'O STJ decidiu, no dia 10/12/2014, que uma causa relativa à violação de Direitos Humanos deve passar da Justiça Estadual para a Justiça Federal, configurando o chamado Incidente de Deslocamento de Competência. A causa trata do desaparecimento de três moradores de rua e da suspeita de tortura contra um quarto indivíduo. Desde a promulgação da Emenda 45, em 2004, essa é a terceira vez que o STJ admite o Incidente de Deslocamento de Competência. De acordo com o que está expressamente previsto na Constituição Federal, a finalidade desse Incidente é o de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 21, 'A', 'garantir o direito de acesso à Justiça.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 21, 'B', 'assegurar o cumprimento de obrigações decorrentes de tratados internacionais de Direitos Humanos dos quais o Brasil seja parte.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 21, 'C', 'combater a morosidade de órgãos da Administração Pública e do Poder Judiciário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 21, 'D', 'combater a corrupção em entes públicos dos Estados e do Distrito Federal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 22, 'No Caso Damião Ximenes (primeiro caso do Brasil na Corte Interamericana de Direitos Humanos), o Brasil foi condenado a investigar e sancionar os responsáveis pela morte de Damião Ximenes, a desenvolver um programa de formação e capacitação para as pessoas vinculadas ao atendimento de saúde mental e a reparação pecuniária da família. Damião Ximenes foi morto, sob tortura, em uma clínica psiquiátrica particular na cidade de Sobral, no Ceará. A condenação recaiu sobre a Federação (União) e não sobre o estado do Ceará ou sobre o município de Sobral, embora ambos tenham algum tipo de responsabilidade sobre o funcionamento da clínica. A responsabilização do governo federal (e não do estadual ou do municipal) aconteceu porque', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 22, 'A', 'estado e município não possuem capacidade jurídica para responder pela violação de direitos humanos praticados por seus agentes.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 22, 'B', 'o Brasil é um estado federativo e, nesses casos, cabe ao governo nacional cumprir todas as disposições da Convenção Americana sobre Direitos Humanos, relacionadas com as matérias sobre as quais exerce competência legislativa e judicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 22, 'C', 'o falecimento de Damião Ximenes aconteceu em uma clínica particular e cabe ao SUS, que é federal, a regulamentação e supervisão do funcionamento de todas as casas de saúde.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 22, 'D', 'a Corte Interamericana de Direitos Humanos possui jurisdição internacional e para que a condenação recaísse sobre um estado ou um município seria necessária a homologação da decisão da Corte pelo Tribunal de Justiça do Ceará.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 23, 'Ricardo, brasileiro naturalizado, mora na cidade do Rio de Janeiro há 9 (nove) anos. Em visita a parentes italianos, conhece Giulia, residente em Roma, com quem passa a ter um relacionamento amoroso. Após 3 (três) anos de namoro a distância, ficam noivos e celebram matrimônio em território italiano. De comum acordo, o casal estabelece seu primeiro domicílio em São Paulo, onde ambos possuem oportunidades de trabalho. À luz das regras de Direito Internacional Privado, veiculadas na Lei de Introdução às Normas do Direito Brasileiro (LINDB), não havendo pacto antenupcial, assinale a opção que indica a legislação que irá reger o regime de bens entre os cônjuges.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 23, 'A', 'Aplicável a Lei italiana, haja vista que nenhum dos cônjuges é brasileiro nato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 23, 'B', 'Aplicável a Lei italiana, em razão do local em que foi realizado o casamento.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 23, 'C', 'Aplicável a Lei brasileira, em razão do domicílio do cônjuge varão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 23, 'D', 'Aplicável a Lei brasileira, porque aqui constituído o primeiro domicílio do casal.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 24, 'Uma carta rogatória foi encaminhada, nos termos da Convenção Interamericana sobre Cartas Rogatórias, para citação de pessoa física domiciliada em São Paulo, para responder a processo de divórcio nos Estados Unidos. A esse respeito, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 24, 'A', 'Não será necessário obter exequatur em função do tratado multilateral ratificado por ambos os países.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 24, 'B', 'O STJ deverá conceder o exequatur, cabendo à justiça estadual cumprir a ordem de citação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 24, 'C', 'A concessão de exequatur caberá ao STJ e seu posterior cumprimento à justiça federal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 24, 'D', 'A concessão de exequatur e seu posterior cumprimento caberão à autoridade central indicada na Convenção Interamericana sobre Cartas Rogatórias.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 25, 'A União instituiu determinado tributo federal e conferiu a uma autarquia as tarefas de fiscalizá-lo e arrecadá-lo. Tendo em vista a situação narrada, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 25, 'A', 'A capacidade tributária ativa (realizar atos de fiscalização e arrecadação) é delegável.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 25, 'B', 'Trata-se de caso de delegação da competência tributária da União.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 25, 'C', 'Não é possível que a União revogue, a qualquer tempo e por ato unilateral, a atribuição que conferiu a tal autarquia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 25, 'D', 'Em eventual discussão judicial proposta por um contribuinte do tributo, a autarquia não terá prazo em dobro para recorrer.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 26, 'Antônio, prestador de serviço de manutenção e reparo de instrumentos musicais, sujeito à incidência do Imposto Sobre Serviços (ISS), deixou de recolher o tributo incidente sobre fato gerador consumado em janeiro de 2013 (quando a alíquota do ISS era de 5% sobre o total auferido pelos serviços prestados e a multa pelo inadimplemento do tributo era de 25% sobre o ISS devido e não recolhido). Em 30 de agosto de 2013, o Município credor aprovou lei que: (a) reduziu para 2% a alíquota do ISS sobre a atividade de manutenção e reparo de instrumentos musicais; e (b) reduziu a multa pelo inadimplemento do imposto incidente nessa mesma atividade, que passou a ser de 10% sobre o ISS devido e não recolhido. Em fevereiro de 2014, o Município X promoveu o lançamento do imposto, exigindo do contribuinte o montante de R$ 25.000,00 – sendo R$ 20.000,00 de imposto (5% sobre R$ 400.000,00, valor dos serviços prestados) e R$ 5.000,00 a título de multa pela falta de pagamento (25% do imposto devido). Sobre a hipótese apresentada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 26, 'A', 'O lançamento está correto em relação ao imposto e à multa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 26, 'B', 'O lançamento está incorreto tanto em relação ao imposto (que deveria observar a nova alíquota de 2%) quanto em relação à multa (que deveria ser de 10% sobre o ISS devido e não recolhido).');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 26, 'C', 'O lançamento está correto em relação à multa, mas incorreto em relação ao imposto (que deveria observar a nova alíquota de 2%).');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 26, 'D', 'O lançamento está correto em relação ao imposto, mas incorreto em relação à multa (que deveria ser de 10% sobre o ISS devido e não recolhido).');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 27, 'A Presidência da República, por meio do Decreto 123, de 1º de janeiro de 2015, aprovou novas alíquotas para o Imposto sobre Produtos Industrializados (IPI), dentro das balizas fiadas na lei tributária, a saber: Cigarro – alíquota de 100% Vestuário – alíquota de 10% Macarrão – alíquota zero Sobre a hipótese, é possível afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 27, 'A', 'o referido decreto é inconstitucional, uma vez que viola o princípio da legalidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 27, 'B', 'o referido decreto é inconstitucional, uma vez que viola o princípio do não confisco.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 27, 'C', 'as alíquotas são diferenciadas em razão da progressividade do IPI.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 27, 'D', 'as alíquotas são diferenciadas em razão do princípio da seletividade do IPI.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 28, 'Prefeito de um determinado município resolveu conceder isenção de IPTU, por 10 (dez) anos, a proprietários de imóveis que sejam portadores de deficiência e que realizem investimento nas condições de acessibilidade de suas propriedades. Com base na situação apresentada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 28, 'A', 'É possível que o município institua a isenção por meio de decreto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 28, 'B', 'Tal isenção constitui caso de suspensão da exigibilidade daqueles créditos tributários.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 28, 'C', 'Somente por meio de lei específica municipal pode ser concedida isenção de IPTU.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 28, 'D', 'A isenção concedida em função de determinadas condições, como é o caso, pode ser revogada a qualquer tempo.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 29, 'O Ministério Público do Estado W ajuizou ação de improbidade administrativa contra um ex-governador, com fundamento no Art. 9º da Lei nº 8.429/1992 (ato de improbidade administrativa que importe enriquecimento ilícito), mesmo passados quase 3 (três) anos do término do mandato e 6 (seis) anos desde a suposta prática do ato de improbidade que lhe é atribuída. Nesse caso,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 29, 'A', 'o ex-governador está sujeito, dentre outras sanções, à perda dos bens ou valores acrescidos ilicitamente ao patrimônio, ao ressarcimento integral do dano e à suspensão dos direitos políticos pelo período de oito a dez anos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 29, 'B', 'a ação de improbidade está fadada ao insucesso, tendo em vista que não podem ser réus de tal demanda aqueles que já não ocupam mandato eletivo e nem cargo, emprego ou função na Administração.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 29, 'C', 'a ação de improbidade está fadada ao insucesso, tendo em vista que já transcorreram mais de 3 (três) anos desde o término do exercício do mandado eletivo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 29, 'D', 'é imprescritível a ação de improbidade destinada à aplicação das sanções previstas na Lei nº 8.429/1992, e, por essa razão, o ex-governador pode sofrer as cominações legais, mesmo após o término do seu mandato.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 30, 'O Estado XYZ pretende criar uma nova universidade estadual sob a forma de fundação pública. Considerando que é intenção do Estado atribuir personalidade jurídica de direito público a tal fundação, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 30, 'A', 'Tal fundação há de ser criada com o registro de seus atos constitutivos, após a edição de lei ordinária autorizando sua instituição.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 30, 'B', 'Tal fundação há de ser criada por lei ordinária específica.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 30, 'C', 'Não é possível a criação de uma fundação pública com personalidade jurídica de direito público.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 30, 'D', 'Tal fundação há de ser criada por lei complementar específica.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 31, 'Marcos Paulo é servidor público federal há mais de 5 (cinco) anos e, durante todo esse tempo, nunca sofreu qualquer sanção administrativa, apesar de serem frequentes suas faltas e seus atrasos ao serviço. No último mês, entretanto, as constantes ausências chamaram a atenção de seu chefe, que, ao buscar a ficha de frequência do servidor, descobriu que Marcos Paulo faltara mais de 90 (noventa) dias no último ano. A respeito do caso apresentado, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 31, 'A', 'Marcos Paulo, servidor público estável, só pode ser demitido após decisão judicial transitada em julgado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 31, 'B', 'Marcos Paulo, servidor público estável, pode ser demitido pela sua inassiduidade após decisão em processo administrativo em que lhe seja assegurada ampla defesa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 31, 'C', 'Marcos Paulo, servidor público estável que nunca sofrera qualquer punição na esfera administrativa, não pode ser demitido em razão de sua inassiduidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 31, 'D', 'Marcos Paulo, servidor público estável, não pode ser demitido em razão de sua inassiduidade, pois esta somente autoriza a aplicação das sanções de advertência e suspensão.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 32, 'O Município C está elaborando edital de licitação para a contratação de serviço de limpeza predial. A respeito do prazo de duração desse contrato, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 32, 'A', 'O prazo de duração do contrato está adstrito à vigência do respectivo crédito orçamentário, sem possibilidade de prorrogação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 32, 'B', 'O contrato de prestação de serviços pode ser celebrado pelo prazo de até 48 meses.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 32, 'C', 'O contrato pode ser celebrado por prazo indeterminado, mantendo-se vigente enquanto não houver melhor preço do que o da proposta vencedora da licitação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 32, 'D', 'O contrato poderá ter a sua duração prorrogada por iguais e sucessivos períodos com vistas à obtenção de preços e condições mais vantajosas para a administração, limitada a sessenta meses.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 33, 'Após celebrar contrato de gestão com uma organização social, a União pretende celebrar, com a mesma organização, contrato de prestação de serviços para a realização de atividades contempladas no contrato de gestão. Com base na hipótese apresentada, assinale a afirmativa correta. (', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 33, 'A', 'É obrigatória a realização de licitação para a celebração do contrato de prestação de serviços. (');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 33, 'B', 'É dispensável a realização de licitação para a celebração do contrato de prestação de serviços. (');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 33, 'C', 'É inexigível a realização de licitação para a celebração do contrato de prestação de serviços. (');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 33, 'D', 'Não é possível celebrar contrato de prestação de serviços com entidade qualificada como organização social.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 34, 'Após dezenas de reclamações dos usuários do serviço de transporte metroviário, o Estado Y determinou a abertura de processo administrativo para verificar a prestação inadequada e ineficiente do serviço por parte da empresa concessionária. Caso se demonstre a inadimplência, como deverá proceder o poder público concedente?', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 34, 'A', 'Declarar, por decreto, a caducidade da concessão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 34, 'B', 'Declarar, por decreto, a encampação do serviço.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 34, 'C', 'Declarar, por decreto, após lei autorizativa, a revogação da concessão.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 34, 'D', 'Declarar, por lei, a anulação do contrato de concessão.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 35, 'Determinada sociedade empresária consulta seu advogado para obter informações sobre as exigências ambientais que possam incidir em seus projetos, especialmente no que tange à apresentação e aprovação de Estudo Prévio de Impacto Ambiental e seu respectivo Relatório (EIA/RIMA). Considerando a disciplina do EIA/RIMA pelo ordenamento jurídico, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 35, 'A', 'O EIA/RIMA é um estudo simplificado, integrante do licenciamento ambiental, destinado a avaliar os impactos ao meio ambiente natural, não abordando impactos aos meios artificial e cultural, pois esses componentes, segundo pacífico entendimento doutrinário e jurisprudencial, não integram o conceito de “meio ambiente”.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 35, 'B', 'O EIA/RIMA é exigido em todas as atividades e empreendimentos que possam causar impactos ambientais, devendo ser aprovado previamente à concessão da denominada Licença Ambiental Prévia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 35, 'C', 'O EIA/RIMA, além de ser aprovado entre as Licenças Ambientais Prévia e de Instalação, tem a sua metodologia e o seu conteúdo regrados exclusivamente por Resoluções do Conselho Nacional do Meio Ambiente (CONAMA), podendo a entidade / o órgão ambiental licenciador dispensá-lo segundo critérios discricionários e independentemente de fundamentação, ainda que a atividade esteja prevista em Resolução CONAMA como passível de EIA/RIMA.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 35, 'D', 'O EIA-RIMA é um instrumento de avaliação de impactos ambientais, de natureza preventiva, exigido para atividades/empreendimentos não só efetiva como potencialmente capazes de causar significativa degradação, sendo certo que a sua publicidade é uma imposição Constitucional (CRFB/1988).');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 36, 'João acaba de adquirir dois imóveis, sendo um localizado em área urbana e outro, em área rural. Por ocasião da aquisição de ambos os imóveis, João foi alertado pelos alienantes de que os imóveis contemplavam Áreas de Preservação Permanente (APP) e de que, por tal razão, ele deveria buscar uma orientação mais especializada, caso desejasse nelas intervir. Considerando a disciplina legal das Áreas de Preservação Permanente (APP), bem como as possíveis preocupações gerais de João, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 36, 'A', 'As APPs não são passíveis de intervenção e utilização, salvo decisão administrativa em sentido contrário de órgão estadual integrante do Sistema Nacional de Meio Ambiente – SISNAMA, uma vez que não há preceitos legais abstratamente prevendo exceções à sua preservação absoluta e integral.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 36, 'B', 'As hipóteses legais de APP, com o advento do denominado “Novo Código Florestal” – Lei nº 12.651/2012 –, foram abolidas em âmbito federal, subsistindo apenas nos casos em que os Estados e Municípios assim as exijam legalmente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 36, 'C', 'As APPs são espaços territoriais especialmente protegidos, comportando exceções legais para fins de intervenção, sendo certo que os Estados e os Municípios podem prever outras hipóteses de APP além daquelas dispostas em normas gerais, inclusive em suas Constituições Estaduais e Leis Orgânicas, sendo que a supressão irregular da vegetação nela situada gera a obrigação do proprietário, possuidor ou ocupante a qualquer título de promover a sua recomposição, obrigação esta de natureza propter rem.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 36, 'D', 'As APPs, assim como as reservas legais, não se aplicam às áreas urbanas, sendo certo que a Lei Federal nº 12.651/2012 (“Novo Código Florestal”), apesar de ter trazido significativas mudanças no seu regime, garantiu as APPs para os imóveis rurais com mais de 100 hectares.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 37, 'Joana e suas quatro irmãs, para comemorar as bodas de ouro de seus pais, contrataram Ricardo para organizar a festa. No contrato ficou acordado que as cinco irmãs arcariam solidariamente com todos os gastos. Ricardo, ao requerer o sinal de pagamento, previamente estipulado no contrato, não obteve sucesso, pois cada uma das irmãs informava que a outra tinha ficado responsável pelo pagamento. Ainda assim, Ricardo cumpriu sua parte do acordado. Ao final da festa, Ricardo foi até Joana para cobrar pelo serviço, sem sucesso. Sobre a situação apresentada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 37, 'A', 'Se Ricardo resolver ajuizar demanda em face somente de Joana, as outras irmãs, ainda assim, permanecerão responsáveis pelo débito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 37, 'B', 'Se Joana pagar o preço total do serviço sozinha, poderá cobrar das outras, ficando sem receber se uma delas se tornar insolvente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 37, 'C', 'Se uma das irmãs de Joana falecer deixando dois filhos, qualquer um deles deverá arcar com o total da parte de sua mãe.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 37, 'D', 'Ricardo deve cobrar de cada irmã a sua quota-parte para receber o total do serviço, uma vez que se trata de obrigação divisível.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 38, 'João Henrique residia com sua companheira Natália em imóvel alugado a ele por Frederico pelo prazo certo de trinta meses, tendo como fiador Waldemar, pai de João Henrique. A união do casal, porém, chegou ao fim, de forma que João Henrique deixou o lar quando faltavam seis meses para o fim do prazo da locação. O locador e o fiador foram comunicados a respeito da saída de João Henrique do imóvel. Sobre o caso apresentado, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 38, 'A', 'Como o locatário era João Henrique, sua saída do imóvel implica a extinção do contrato de locação, podendo Frederico exigir, imediatamente, que Natália o desocupe.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 38, 'B', 'Como João Henrique era o locatário, sua saída permite que Natália continue residindo no imóvel apenas até o término do prazo contratual, momento em que o contrato se extingue, sem possibilidade de renovação, salvo nova convenção entre Natália e Frederico.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 38, 'C', 'Com a saída do locatário do imóvel, a locação prossegue automaticamente tendo Natália como locatária, porém a fiança prestada por Waldemar caduca, permitindo a Frederico exigir de Natália o oferecimento de nova garantia, sob pena de resolução do contrato.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 38, 'D', 'Com a saída do locatário, a locação prossegue com Natália, permitido a Waldemar exonerar-se da fiança em até trinta dias da data em que for cientificado da saída do seu filho do imóvel; ainda assim, a exoneração só produzirá efeitos cento e vinte dias depois de notificado o locador.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 39, 'Vitor e Paula celebram entre si, por escritura particular levada a registro em cartório de títulos e documentos, contrato de mútuo por meio do qual Vitor toma emprestada de Paula a quantia de R$ 10.000,00, obrigando-se a restituir o montante no prazo de três meses. Em garantia da dívida, Vitor constitui em favor de Paula, por meio de instrumento particular, direito real de penhor sobre uma joia de que é proprietário. Vencido o prazo estabelecido para o pagamento da dívida, Vitor procura Paula e explica que não dispõe de dinheiro para quitar o débito. Propõe então que, em vez da quantia devida, Paula receba, em pagamento da dívida, a propriedade da coisa empenhada. Assinale a opção que indica a orientação correta a ser transmitida a Paula.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 39, 'A', 'Para ter validade, o acordo sugerido por Vitor deve ser celebrado mediante escritura pública.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 39, 'B', 'O acordo sugerido por Vitor não tem validade, uma vez que constitui espécie de pacto proibido pela lei.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 39, 'C', 'Para ter validade, o acordo sugerido deve ser homologado em juízo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 39, 'D', 'O acordo sugerido por Vitor é válido, uma vez que constitui espécie de pacto cuja licitude é expressamente reconhecida pela lei.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 40, 'Roberto e Ana casaram-se, em 2005, pelo regime da comunhão parcial de bens. Em 2008, Roberto ganhou na loteria e, com os recursos auferidos, adquiriu um imóvel no Recreio dos Bandeirantes. Em 2014, Roberto foi agraciado com uma casa em Santa Teresa, fruto da herança de sua tia. Em 2015, Roberto e Ana se separaram. Tendo em vista o regime de bens do casamento, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 40, 'A', 'Os imóveis situados no Recreio dos Bandeirantes e em Santa Teresa são bens comuns e, por isso, deverão ser partilhados em virtude da separação do casal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 40, 'B', 'Apenas o imóvel situado no Recreio dos Bandeirantes deve ser partilhado, sendo o imóvel situado em Santa Teresa bem particular de Roberto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 40, 'C', 'Apenas o imóvel situado em Santa Teresa deve ser partilhado, sendo o imóvel situado no Recreio dos Bandeirantes excluído da comunhão, por ter sido adquirido com o produto de bem advindo de fato eventual.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 40, 'D', 'Nenhum dos dois imóveis deverá ser partilhado, tendo em vista que ambos são bens particulares de Roberto.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 41, 'Renato é proprietário de um imóvel e o coloca à venda, atraindo o interesse de Mário. Depois de algumas visitas ao imóvel e conversas sobre o seu valor, Renato e Mário, acompanhados de corretor, realizam negócio por preço certo, que deveria ser pago em três parcelas: a primeira, paga naquele ato a título de sinal e princípio de pagamento, mediante recibo que dava o negócio por concluído de forma irretratável; a segunda deveria ser paga em até trinta dias, contra a exibição das certidões negativas do vendedor; a terceira seria paga na data da lavratura da escritura definitiva, em até noventa dias a contar do fechamento do negócio. Antes do pagamento da segunda parcela, Mário celebra, com terceiros, contratos de promessa de locação do imóvel por temporada, recebendo a metade de cada aluguel antecipadamente. Renato, ao tomar conhecimento de que Mário havia celebrado as promessas de locação por temporada, percebeu que o imóvel possuía esse potencial de exploração. Em virtude disso, Renato arrependeu-se do negócio e, antes do vencimento da segunda parcela do preço, notificou o comprador e o corretor, dando o negócio por desfeito. Com base na hipótese formulada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 41, 'A', 'O vendedor perde o sinal pago para o comprador, porém nada mais lhe pode ser exigido, não sendo devida a comissão do corretor, já que o negócio foi desfeito antes de aperfeiçoar-se.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 41, 'B', 'O vendedor perde o sinal pago para o comprador, porém nada mais lhe pode ser exigido pelo comprador. Contudo, é devida a comissão do corretor, não obstante o desfazimento do negócio antes de aperfeiçoar-se.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 41, 'C', 'O vendedor perde o sinal pago e o comprador pode exigir uma indenização pelos prejuízos a que a desistência deu causa, se o seu valor superar o do sinal dado, não sendo devida a comissão do corretor, já que o negócio foi desfeito antes de aperfeiçoar-se.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 41, 'D', 'O vendedor perde o sinal pago e o comprador pode exigir uma indenização pelos prejuízos a que a desistência deu causa, se o seu valor superar o do sinal dado, sendo devida a comissão do corretor, não obstante o desfazimento do negócio antes de aperfeiçoar-se.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 42, 'Por meio de contrato verbal, João alugou sua bicicleta a José, que se comprometeu a pagar o aluguel mensal de R$ 100,00 (cem reais), bem como a restituir a coisa alugada ao final do sexto mês de locação. Antes de esgotado o prazo do contrato de locação, João deseja celebrar contrato de compra e venda com Otávio, de modo a transmitir imediatamente a propriedade da bicicleta. Não obstante a coisa permanecer na posse direta de José, entende-se que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 42, 'A', 'o adquirente Otávio, caso venda a bicicleta antes de encerrado o prazo da locação, deve obrigatoriamente depositar o preço em favor do locatário José.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 42, 'B', 'João não pode celebrar contrato de compra e venda da bicicleta antes de encerrado o prazo da locação celebrada com José.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 42, 'C', 'é possível transmitir imediatamente a propriedade para Otávio, por meio da estipulação, no contrato de compra e venda, da cessão do direito à restituição da coisa em favor de Otávio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 42, 'D', 'é possível transmitir imediatamente a propriedade para Otávio, por meio da estipulação, no contrato de compra e venda, do constituto possessório em favor de Otávio.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 43, 'Fabiana e Mauro são casados pelo regime da separação convencional de bens e possuem dois filhos: Amanda e Pedro, de 19 e 16 anos, respectivamente. Mauro é filho de José, que se encontra com 65 anos. Mauro sofreu um acidente automobilístico e, em razão da violência do acidente, está em estado de coma, impossibilitado de exercer os atos da vida civil, razão pela qual sua interdição tornou-se necessária. Diante dos fatos narrados, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 43, 'A', 'Fabiana, em razão do regime de bens que rege o casamento, não poderá ser nomeada curadora de Mauro.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 43, 'B', 'Como Mauro possui ascendente vivo e capaz, este será nomeado seu curador, na forma da lei.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 43, 'C', 'A filha de Mauro, por ser maior e capaz, será nomeada sua curadora, na forma da lei.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 43, 'D', 'Fabiana será nomeada curadora de Mauro, na forma da lei.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 44, 'Isabela e Matheus pretendem ingressar com ação judicial própria a fim de adotar a criança P., hoje com 4 anos, que está sob guarda de fato do casal desde quando tinha 1 ano de idade. Os pais biológicos do infante são conhecidos e não se opõem à referida adoção, até porque as famílias mantêm convívio em datas festivas, uma vez que Isabela e Matheus consideram importante que P. conheça sua matriz biológica e mantenha convivência com os membros de sua família originária. Partindo das diretrizes impostas pelo ECA e sua interpretação à luz da norma civilista aplicáveis à situação narrada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 44, 'A', 'Durante o processo de adoção, Isabela, que reside fora do país, pode, mediante procuração, constituir Matheus como seu mandatário com poderes especiais para representar sua esposa e ajuizar a ação como adoção conjunta.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 44, 'B', 'Dispensável a oitiva dos pais biológicos em audiência, desde que eles manifestem concordância com o pedido de adoção por escritura pública ou declaração de anuência com firma reconhecida.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 44, 'C', 'Concluído o processo de adoção com observância aos critérios de regularidade e legalidade, caso ocorra o evento da morte de Isabela e Matheus antes de P. atingir a maioridade civil, ainda assim não se reestabelecerá o poder familiar dos pais biológicos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 44, 'D', 'A adoção é medida excepcional, que decorre de incompatibilidade de os pais biológicos cumprirem os deveres inerentes ao poder familiar, motivo pelo qual, mesmo os pais de P. sendo conhecidos, a oitiva deles no curso do processo é mera faculdade e pode ser dispensada.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 45, 'J., com 11 anos, L., com 12 anos, e M., com 13 anos de idade, são alunos do 8º ano do ensino fundamental de uma conceituada escola particular. Os três, desde que foram estudar na mesma turma, passaram a causar diversos problemas para o transcurso normal das aulas, tais como: escutar música; conversar; dormir; colocar os pés nas mesas e não desligar o aparelho celular. O professor de matemática, inconformado com a conduta desrespeitosa dos alunos, repreende-os, avisando que os encaminhará para a direção da escola. Ato contínuo, os alunos reagem da seguinte forma: J. chama o professor de “velho idiota”; L. levanta e sai da sala no meio da aula; e M. ameaça matá-lo. Diante dos atos de indisciplina dos três alunos, a direção da escola entra em contato com o seu departamento jurídico para, com base no Estatuto da Criança e do Adolescente, receber a orientação de como proceder. Com base na hipótese apresentada, assinale a opção que apresenta a orientação recebida pela direção escolar.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 45, 'A', 'Os atos de indisciplina praticados por J., L. e M. deverão ser coibidos pela própria direção escolar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 45, 'B', 'J. e M. praticaram atos infracionais. J. deverá ser encaminhado ao Conselho Tutelar e M. para a autoridade policial. A indisciplina de L. deverá ser coibida pela própria direção escolar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 45, 'C', 'J., L. e M. praticaram atos infracionais e deverão ser encaminhados para a autoridade policial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 45, 'D', 'J. e M. praticaram atos infracionais. Ambos deverão ser encaminhados para a autoridade policial. A indisciplina de L. deverá ser coibida pela própria direção escolar.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 46, 'Dulce, cinquenta e oito anos de idade, fumante há três décadas, foi diagnosticada como portadora de enfisema pulmonar. Trata-se de uma doença pulmonar obstrutiva crônica caracterizada pela dilatação excessiva dos alvéolos pulmonares, que causa a perda da capacidade respiratória e uma consequente oxigenação insuficiente. Em razão do avançado estágio da doença, foi prescrito como essencial o tratamento de suplementação de oxigênio. Para tanto, Joana, filha de Dulce, adquiriu para sua mãe um aparelho respiratório na loja Saúde e Bem-Estar. Porém, com uma semana de uso, o produto parou de funcionar. Joana procurou imediatamente a loja para substituição do aparelho, oportunidade na qual foi informada pela gerente que deveria aguardar o prazo legal de trinta dias para conserto do produto pelo fabricante. Com base no caso narrado, em relação ao Código de Proteção e Defesa do Consumidor, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 46, 'A', 'Está correta a orientação da vendedora. Joana deverá aguardar o prazo legal de trinta dias para conserto e, caso não seja sanado o vício, exigir a substituição do produto, a devolução do dinheiro corrigido monetariamente ou o abatimento proporcional do preço.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 46, 'B', 'Joana não é consumidora destinatária final do produto, logo tem apenas direito ao conserto do produto durável no prazo de noventa dias, mas não à devolução da quantia paga.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 46, 'C', 'Joana não precisa aguardar o prazo legal de trinta dias para conserto, pois tem direito de exigir a substituição imediata do produto, em razão de sua essencialidade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 46, 'D', 'Na impossibilidade de substituição do produto por outro da mesma espécie, Joana poderá optar por um modelo diverso, sem direito à restituição de eventual diferença de preço, e, se este for de valor maior, não será devida por Joana qualquer complementação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 47, 'Hugo colidiu com seu veículo e necessitou de reparos na lataria e na pintura. Para tanto, procurou, por indicação de um amigo, os serviços da Oficina Mecânica M, oportunidade na qual lhe foi ofertado orçamento escrito, válido por 15 (quinze) dias, com o valor da mão de obra e dos materiais a serem utilizados na realização do conserto do automóvel. Hugo, na certeza da boa indicação, contratou pela primeira vez com a Oficina. Considerando as regras do Código de Proteção e Defesa do Consumidor, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 47, 'A', 'Segundo a lei do consumidor, o orçamento tem prazo de validade obrigatório de 10 (dez) dias, contados do seu recebimento pelo consumidor Hugo. Logo, no caso, somente durante esse período a Oficina Mecânica M estará vinculada ao valor orçado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 47, 'B', 'Uma vez aprovado o orçamento pelo consumidor, os contraentes estarão vinculados, sendo correto afirmar que Hugo não responderá por quaisquer ônus ou acréscimos no valor dos materiais orçados; contudo, ele poderá vir a responder pela necessidade de contratação de terceiros não previstos no orçamento prévio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 47, 'C', 'Se o serviço de pintura contratado por Hugo apresentar vícios de qualidade, é correto afirmar que ele terá tríplice opção, à sua escolha, de exigir da oficina mecânica: a reexecução do serviço sem custo adicional; a devolução de eventual quantia já paga, corrigida monetariamente, ou o abatimento do preço de forma proporcional.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 47, 'D', 'A lei consumerista considera prática abusiva a execução de serviços sem a prévia elaboração de orçamento, o que pode ser feito por qualquer meio, oral ou escrito, exigindo- se, para sua validade, o consentimento expresso ou tácito do consumidor.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 48, 'Feijó recebeu de Moura um cheque com cruzamento especial no valor de R$ 2.300,00 (dois mil e trezentos reais). Acerca das disposições legais que disciplinam tal espécie de cheque, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 48, 'A', 'O cheque com cruzamento especial pode ser pago em dinheiro no estabelecimento contra o qual foi sacado ou mediante apresentação a uma câmara de compensação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 48, 'B', 'O cruzamento especial não pode ser convertido em geral e a inutilização do cruzamento ou a do nome do banco é reputada como não existente.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 48, 'C', 'A aposição de vários cruzamentos especiais invalida o cheque, exceto se o portador, no primeiro cruzamento, indicar o mesmo banco que o sacado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 48, 'D', 'O cheque com cruzamento especial obriga o sacado a debitar a quantia indicada no título da conta do emitente e a reservá-la em benefício do portador legitimado, durante o prazo de apresentação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 49, 'São João da Baliza Transporte Rodoviário Ltda. sacou duplicata de prestação de serviços no valor de R$ 32.000,00 (trinta e dois mil reais) para recebimento do frete decorrente do transporte de cargas entre ela e Supermercados Caracaraí Ltda. EPP. Diante do inadimplemento do pagamento do frete, a sacadora levou a duplicata a protesto, sem aceite, com vistas a instruir pedido de falência do sacado. Com base nas informações do enunciado, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 49, 'A', 'Essa duplicata não aceita não é título hábil para instruir pedido de falência, ainda que protestada e comprovada a prestação dos serviços.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 49, 'B', 'Essa duplicata não aceita, mas protestada, é título hábil para instruir pedido de falência, comprovada a prestação dos serviços.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 49, 'C', 'Essa duplicata de prestação de serviços é título hábil para instruir pedido de falência, caso esteja aceita, protestada e tenha o sacador comprovado a prestação dos serviços.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 49, 'D', 'Essa duplicata não é título hábil para instruir pedido de falência do destinatário porque o documento apropriado para a cobrança do frete é o conhecimento de transporte.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 50, 'Pretendendo aderir a um sistema de franquia empresarial, o microempresário individual SF consulta sua advogada sobre as disposições legais referentes a esse contrato. Assinale, dentre as afirmativas a seguir, a que apresenta a informação correta prestada pela advogada.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 50, 'A', 'O franqueador é obrigado a incluir na circular de oferta de franquia informação em relação ao território de atuação do franqueado, especificando a possibilidade de o franqueado realizar vendas ou prestar serviços fora de seu território, ou realizar exportações.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 50, 'B', 'Em razão do sigilo dos instrumentos de escrituração, dos balanços e das demonstrações financeiras dos empresários, o franqueador não é obrigado a incluir tais documentos nas informações da circular de oferta de franquia.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 50, 'C', 'Tratando-se de franqueador ou franqueado enquadrado como microempreendedor individual, microempresa ou empresa de pequeno porte, é dispensável a presença no contrato de testemunhas e terá validade independentemente de ser levado a registro perante cartório ou órgão público.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 50, 'D', 'Se o franqueador veicular informações falsas na circular de oferta de franquia, o franqueado não poderá arguir a anulabilidade do contrato, apenas das cláusulas pertinentes, mas poderá exigir devolução das quantias que já houver pago, a título de taxa de filiação e royalties, devidamente corrigidas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 51, 'O contrato da sociedade do tipo simples Angélica Médicos Associados é omisso quanto à possibilidade de sucessão por morte de sócio. Inocência, uma das sócias, consulta você para saber qual a regra prevista no Código Civil para esse caso. Você respondeu corretamente que, com a morte de sócio,', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 51, 'A', 'opera-se a dissolução da sociedade de pleno direito. Caberá a liquidação da quota do sócio falecido, cujo valor, considerado pelo montante efetivamente realizado, será apurado, com base no último balanço aprovado, salvo disposição contratual em contrário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 51, 'B', 'opera-se a sucessão dos herdeiros do sócio falecido na sociedade. Os herdeiros poderão pleitear o levantamento de balanço de resultado econômico para verificação da situação patrimonial da sociedade à data do óbito, salvo disposição contratual em contrário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 51, 'C', 'opera-se a resolução da sociedade em relação ao sócio falecido. Caberá a liquidação da quota do falecido, cujo valor, considerado pelo montante efetivamente realizado, será apurado, com base na situação patrimonial da sociedade à data do óbito, verificada em balanço especialmente levantado, salvo disposição contratual em contrário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 51, 'D', 'opera-se a substituição do sócio falecido mediante acordo dos sócios remanescentes com os herdeiros. Os herdeiros poderão pleitear a liquidação da quota com base no valor econômico da sociedade, a ser apurado em avaliação por três peritos ou por sociedade especializada, mediante laudo fundamentado, salvo disposição contratual em contrário.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 52, 'Calçados Machadinho Ltda. requereu sua recuperação judicial e o pedido foi devidamente processado. O devedor não alterou, no plano de recuperação, o valor ou as condições originais de pagamento do crédito de Curtume Arroio do Sal Ltda. EPP, referentes ao contrato de fornecimento de couro sintético, no valor de R$ 288.000,00 (duzentos e oitenta e oito mil reais). Com base nessas informações e nas disposições da Lei nº 11.101/2005, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 52, 'A', 'A credora não terá direito a voto nas assembleias de credores realizadas durante a recuperação judicial e o crédito não será considerado para fins de verificação de quórum de deliberação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 52, 'B', 'O crédito será novado com a concessão da recuperação judicial, após a aprovação do plano pela assembleia de credores, como todos os demais créditos sujeitos à recuperação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 52, 'C', 'A credora poderá votar nas assembleias de credores realizadas durante a recuperação, com base no valor de seu crédito, na classe dos credores microempresários e empresários de pequeno porte (Classe 4).');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 52, 'D', 'A partir do processamento da recuperação judicial, é permitido à credora ajuizar ação de cobrança em face do devedor pela manutenção das condições originais de pagamento do crédito no plano de recuperação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 53, 'Alan ajuizou medida cautelar preparatória em face de Roberta, obtendo deferimento de pedido liminar para indisponibilizar a venda de veículos de propriedade da ré. De posse da decisão liminar, Alan protocolizou ofício junto ao órgão competente em 30 de janeiro, tendo a liminar sido efetivada em 10 de fevereiro, ou seja, quatro dias antes da citação de Roberta. As datas citadas eram dias úteis. Com base na hipótese narrada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 53, 'A', 'O ajuizamento da ação principal dentro do prazo legal veda ao magistrado revogar a decisão liminar antes da sentença de mérito.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 53, 'B', 'O ajuizamento da ação principal no dia 14 de março acarreta a perda da eficácia da liminar deferida e a extinção da medida cautelar.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 53, 'C', 'A eventual falta de diligência de Alan ao inobservar o prazo legal para execução da decisão liminar acarretará a automática extinção dos processos cautelar e principal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 53, 'D', 'O indeferimento do pedido acautelatório liminar formulado por Alan obsta o ajuizamento da ação principal, por falta de interesse.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 54, 'Henrique fora condenado pelo juízo da 10ª Vara Cível da Comarca da Capital do Rio de Janeiro ao pagamento de indenização por danos morais causados a Marlon, no valor de R$ 100.000,00, tendo tal decisão transitada em julgado. Na fase de cumprimento de sentença, não houve o pagamento voluntário da quantia, nem foram encontrados bens no foro da causa, razão pela qual procedeu-se à avaliação e penhora de imóvel de veraneio de Henrique, situado no Guarujá/SP, mediante carta precatória. O Oficial de Justiça, mesmo certificando em seu laudo não possuir o conhecimento especializado necessário para o ato, avaliou o imóvel em R$ 150.000,00. Nesse caso, a impugnação ao cumprimento de sentença que verse unicamente o vício de avaliação', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 54, 'A', 'poderá ser oferecida no juízo deprecante ou deprecado, sendo o juízo deprecante o competente para julgá-la.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 54, 'B', 'poderá ser oferecida no juízo deprecante ou deprecado, sendo o juízo deprecado o competente para julgá-la.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 54, 'C', 'deverá ser oferecida no juízo deprecado, sendo o juízo deprecante o competente para julgá-la.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 54, 'D', 'deverá ser oferecida no juízo deprecante, sendo o juízo deprecado o competente para julgá-la.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 55, 'X contratou um plano de saúde com cobertura integral e sem carência junto à operadora Saúde 100%, em 19 de outubro de 2012. Seis meses depois, precisou se submeter a uma cirurgia na coluna, mas o plano se negou a cobri-la, sob alegação de que tal procedimento não estava previsto em contrato. Inconformado, X ajuizou ação visando ao cumprimento forçado da obrigação, demanda essa distribuída perante a 10ª Vara Cível da comarca do Rio de Janeiro, de titularidade do magistrado Y. Após regular tramitação, o pedido autoral foi julgado procedente, tendo em sentença sido fixado o prazo de 10 dias para a efetivação da cirurgia, sob pena de multa de R$ 1.000,00 por dia de atraso. Três meses depois do trânsito em julgado, e ainda não tendo sido cumprida a obrigação, X requereu a majoração da multa diária, pedido este indeferido pelo juiz Y, sob alegação de estar impedido de atuar por força da coisa julgada material. Sobre os fatos, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 55, 'A', 'O magistrado agiu corretamente, cabendo ao autor apenas cobrar a multa vencida.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 55, 'B', 'A multa poderia ser aumentada, ou até mesmo substituída por outra medida de apoio mais efetiva, a fim de concretizar o direito do autor.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 55, 'C', 'A multa somente poderia ser aumentada se o autor conseguisse modificar a sentença por meio de uma ação rescisória.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 55, 'D', 'A multa não poderia ser aumentada, mas o juiz poderia condenar a empresa ré a pagar danos morais ao autor.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 56, 'Márcia trafegava regularmente a 40 km/h em uma rua da cidade de Salvador/BA quando seu carro foi abalroado pelo veículo de Tânia que, ao atender a uma ligação do telefone celular enquanto dirigia, perdeu a direção e invadiu a pista contrária de rolamento, causando o acidente. Acalmados os ânimos, as partes não chegaram a um acordo, pelo que Márcia ajuizou, perante a 2ª Vara Cível de Salvador/BA, uma ação de reparação de danos materiais, danos morais e lucros cessantes contra Tânia, que, após ser regularmente citada, contestou todos os pedidos autorais, alegando não ter dado causa ao acidente. Em sentença, após o tramitar processual em que foram cumpridas todas as exigências procedimentais, o magistrado julga procedentes os pedidos de danos materiais e de danos morais, rejeitando, porém, o de pedido de lucros cessantes, por entender inexistirem provas desse dano alegado, tendo tal sentença transitada em julgado em 19/10/2012. Sobre os fatos descritos, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 56, 'A', 'Márcia poderá propor uma nova demanda com o objetivo de obter os lucros cessantes, desde que apresente, nesse novo processo, as provas da ocorrência desse dano.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 56, 'B', 'Se Tânia quiser se valer de uma ação rescisória, terá somente até o dia 19/10/2013 para fazê-lo, sob pena de decadência.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 56, 'C', 'Admitindo-se a hipótese de que Tânia descobrisse que o juiz é irmão de Márcia, ela poderia se valer de uma ação anulatória para fazer cessar os efeitos da sentença, haja vista a falta de imparcialidade do julgador.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 56, 'D', 'Eventual ação rescisória proposta por Tânia não impede a execução da decisão da sentença por parte de Márcia, ainda que Tânia demonstre que a sentença foi injusta.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 57, 'Dominique, cidadão francês, foi parte em procedimento arbitral que teve como sede uma câmara localizada em Paris, na França, tendo este também sido o local onde a sentença foi proferida. Ele obteve êxito em sua pretensão, que foi julgada conforme a legislação francesa, e, agora, deseja receber da parte sucumbente os valores a que faz jus. A parte devedora é brasileira e possui patrimônio no Brasil, sendo a condenação de natureza cível. Ele procura você para, como advogado(a), orientá-lo. Assinale a opção que indica o procedimento que Dominique deve adotar.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 57, 'A', 'Iniciar procedimento de cumprimento de sentença perante uma das varas cíveis da comarca onde está localizada a devedora, uma vez que a sentença arbitral, conforme legislação processual brasileira, é titulo executivo judicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 57, 'B', 'Ajuizar ação de execução perante uma das varas cíveis da comarca onde está localizada a devedora, uma vez que a sentença arbitral, conforme legislação processual brasileira, é titulo executivo extrajudicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 57, 'C', 'Dar início aos trâmites para recebimento dos valores perante os tribunais de Paris, na França, uma vez que esse foi o local onde foi conduzido o procedimento de arbitragem.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 57, 'D', 'Obter, preliminarmente, a homologação da sentença arbitral perante o Superior Tribunal de Justiça, uma vez que, pelas características narradas na questão, trata-se de sentença arbitral estrangeira.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 58, 'Edgar pleiteou a remoção da inventariante Joana, nomeada nos autos do processo de inventário dos bens deixados por morte de sua genitora Maria, argumentando que a inventariante não prestou as primeiras declarações no prazo legal e não está defendendo os interesses do espólio. Acerca do incidente de remoção de inventariante e as regras previstas no Código de Processo Civil, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 58, 'A', 'O incidente de remoção de inventariante, proposto por Edgar, deverá tramitar nos próprios autos da ação de inventário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 58, 'B', 'O juiz, ao receber o requerimento de remoção de inventariante, deverá, conforme previsão expressa do CPC, afastar Joana de suas funções imediatamente e, em seguida, determinar a sua intimação para defender-se e produzir provas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 58, 'C', 'Acolhido o pedido de remoção da inventariante Joana, o magistrado deverá nomear, prioritariamente, Edgar, em razão de ser o autor do requerimento.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 58, 'D', 'Removida a inventariante Joana, esta deverá entregar imediatamente ao substituto os bens do espólio de Maria e, se deixar de fazê-lo, será compelida mediante mandado de busca e apreensão ou imissão de posse, conforme se tratar de bem móvel ou imóvel.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 59, 'Mário subtraiu uma TV do seu local de trabalho. Ao chegar em casa com a coisa subtraída, é convencido pela esposa a devolvê-la, o que efetivamente vem a fazer no dia seguinte, quando o fato já havia sido registrado na delegacia. O comportamento de Mário, de acordo com a teoria do delito, configura', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 59, 'A', 'desistência voluntária, não podendo responder por furto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 59, 'B', 'arrependimento eficaz, não podendo responder por furto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 59, 'C', 'arrependimento posterior, com reflexo exclusivamente no processo dosimétrico da pena.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 59, 'D', 'furto, sendo totalmente irrelevante a devolução do bem a partir de convencimento da esposa.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 60, 'Glória é contratada como secretária de Felipe, um grande executivo de uma sociedade empresarial. Felipe se apaixona por Glória, mas ela nunca lhe deu atenção fora daquela necessária para a profissão. Felipe, então, simula a existência de uma reunião de negócios e pede para que a secretária fique no local para auxiliá-lo. À noite, Glória comparece à sala do executivo acreditando que ocorreria a reunião, quando é surpreendida por este, que coloca uma faca em seu pescoço e exige a prática de atos sexuais, sendo, em razão do medo, atendido. Após o ato, Felipe afirmou que Glória deveria comparecer normalmente ao trabalho no dia seguinte e ainda lhe entregou duas notas de R$ 100,00. Diante da situação narrada, é correto afirmar que Felipe deverá responder pela prática do crime de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 60, 'A', 'violação sexual mediante fraude.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 60, 'B', 'assédio sexual.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 60, 'C', 'favorecimento da prostituição ou outra forma de exploração sexual.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 60, 'D', 'estupro.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 61, 'No ano de 2014, Bruno, Bernardo e Bianca se uniram com a intenção de praticar, reiteradamente, a contravenção penal de jogo do bicho. Para tanto, reuniam-se toda quarta-feira e decidiam em quais locais o jogo do bicho seria explorado. Chegaram, efetivamente, em uma oportunidade, a explorar o jogo do bicho em determinado estabelecimento. Considerando apenas as informações narradas, Bruno, Bernardo e Bianca responderão', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 61, 'A', 'pela contravenção penal do jogo do bicho, apenas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 61, 'B', 'pela contravenção penal do jogo do bicho e pelo crime de associação criminosa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 61, 'C', 'pela contravenção penal do jogo do bicho e pelo crime de organização criminosa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 61, 'D', 'pelo crime de associação criminosa, apenas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 62, 'Cacau, de 20 anos, moça pacata residente em uma pequena fazenda no interior do Mato Grosso, mantém um relacionamento amoroso secreto com Noel, filho de um dos empregados de seu pai. Em razão da relação, fica grávida, mas mantém a situação em segredo pelo temor que tinha de seu pai. Após o nascimento de um bebê do sexo masculino, Cacau, sem que ninguém soubesse, em estado puerperal, para ocultar sua desonra, leva a criança para local diverso do parto e a deixa embaixo de uma árvore no meio da fazenda vizinha, sem prestar assistência devida, para que alguém encontrasse e acreditasse que aquele recém-nascido fora deixado por desconhecido. Apesar de a fazenda vizinha ser habitada, ninguém encontra a criança nas 06 horas seguintes, vindo o bebê a falecer. A perícia confirmou que, apesar do estado puerperal, Cacau era imputável no momento dos fatos. Considerando a situação narrada, é correto afirmar que Cacau deverá ser responsabilizada pelo crime de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 62, 'A', 'abandono de incapaz qualificado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 62, 'B', 'homicídio doloso.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 62, 'C', 'infanticídio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 62, 'D', 'exposição ou abandono de recém-nascido qualificado.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 63, 'Maria mantém relacionamento clandestino com João. Acreditando estar grávida, procura o seu amigo Pedro, que é auxiliar de enfermagem, e implora para que ele faça o aborto. Pedro, que já auxiliou diversas cirurgias legais de aborto, acreditando ter condições técnicas de realizar o ato sozinho, atende ao pedido de sua amiga, preocupado com a situação pessoal de Maria, que não poderia assumir a gravidez por ela anunciada. Durante a cirurgia, em razão da imperícia de Pedro, Maria vem a falecer, ficando apurado que, na verdade, ela não estava grávida. Em razão do fato narrado, Pedro deverá responder pelo crime de', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 63, 'A', 'aborto tentado com consentimento da gestante qualificado pelo resultado morte.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 63, 'B', 'aborto tentado com consentimento da gestante.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 63, 'C', 'homicídio culposo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 63, 'D', 'homicídio doloso.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 64, 'Vinícius, primário e de bons antecedentes e regularmente habilitado, dirigia seu veículo em rodovia na qual a velocidade máxima permitida era de 80 km/h. No banco do carona estava sua namorada Estefânia. Para testar a potência do automóvel, ele passou a dirigir a 140 km/h, acabando por perder o controle do carro, vindo a cair em um barranco. Devido ao acidente, Estefânia sofreu lesão corporal e foi socorrida por policiais rodoviários. No marcador do carro ficou registrada a velocidade desenvolvida. Apesar do ferimento sofrido, a vítima afirmou não querer ver o autor processado por tal comportamento imprudente. Apresentado o inquérito ao Ministério Público, foi oferecida denúncia contra Vinícius pela prática do injusto do Art. 303 da Lei nº 9503/97 (Código de Trânsito Brasileiro), que prevê a pena de 06 meses a 02 anos de detenção e a suspensão ou proibição da permissão ou da habilitação para dirigir veículo automotor. Considerando o acima exposto, a defesa de Vinícius deverá requerer', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 64, 'A', 'a extinção do processo por não ter o Ministério Público legitimidade para oferecer denúncia, em razão da ausência de representação da vítima.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 64, 'B', 'a realização de audiência de composição civil.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 64, 'C', 'a realização de audiência para proposta de transação penal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 64, 'D', 'a suspensão condicional do processo, caso a denúncia seja recebida.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 65, 'Bruna foi presa em flagrante e denunciada pela prática de um crime de falsificação de documento público. Na ocasião da prisão, foi apreendida uma mochila que estava dentro do veículo de Bruna, sendo que em seu interior existiam algumas joias. Diante da natureza do crime apurado, não existe mais interesse na mochila apreendida com as joias para o desenrolar do processo. Cláudia, colega de trabalho de Bruna, requer a restituição desses bens, alegando ser proprietária. Existe, porém, dúvida quanto ao direito da reclamante. Considerando as informações narradas na hipótese, é correto afirmar que', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 65, 'A', 'a restituição poderá ser ordenada pela autoridade policial ou pelo juiz, sempre ouvido o Ministério Público.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 65, 'B', 'o pedido de restituição não deverá ser autuado em autos em apartado.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 65, 'C', 'havendo dúvida sobre o verdadeiro dono, não superada no incidente, o juiz remeterá as partes para o juízo cível, ordenando o depósito das coisas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 65, 'D', 'não caberá produção de provas no incidente de restituição.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 66, 'O Ministério Público ofereceu denúncia em face de Cristiano, Luiz e Leonel pela prática do crime de associação para o tráfico. Na audiência designada para realização dos interrogatórios, Cristiano, preso em outra unidade da Federação, foi interrogado através de vídeoconferência. Luiz foi interrogado na presença física do magistrado e respondeu às perguntas realizadas. Já Leonel optou por permanecer em silêncio. Sobre o interrogatório, considerando as informações narradas, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 66, 'A', 'O interrogatório judicial, notadamente após o advento da Lei nº 10.792/2003, deve ser interpretado apenas como meio de prova e não também como ato de defesa dos acusados.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 66, 'B', 'Luiz, ainda que não impute crime a terceiro, não poderá mentir sobre os fatos a ele imputados, apesar de poder permanecer em silêncio.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 66, 'C', 'A defesa técnica de Cristiano não poderá, em hipótese alguma, formular perguntas para o corréu Luiz.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 66, 'D', 'O interrogatório por vídeoconferência de Cristiano pode ser considerado válido se fundamentado, pelo magistrado, no risco concreto de fuga durante o deslocamento.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 67, 'Estando preso e cumprindo pena na cidade de Campos, interior do estado do Rio de Janeiro, Paulo efetua ligação telefônica para a casa de Maria, localizada na cidade de Niterói, no mesmo Estado, anunciando o falso sequestro do filho desta e exigindo o depósito da quantia de R$ 2.000,00 (dois mil reais), a ser efetuado em conta bancária na cidade do Rio de Janeiro. Maria, atemorizada, efetua a transferência do respectivo valor, no mesmo dia, de sua conta-corrente de uma agência bancária situada em São Gonçalo. Descoberto o fato e denunciado pelo crime de extorsão, assinale a opção que indica o juízo competente para o julgamento.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 67, 'A', 'Vara Criminal de Campos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 67, 'B', 'Vara Criminal de Niterói.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 67, 'C', 'Vara Criminal de São Gonçalo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 67, 'D', 'Vara Criminal do Rio de Janeiro.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 68, 'Determinada autoridade policial recebeu informações de vizinhos de Lucas dando conta de que ele possuía arma de fogo calibre.38 em sua casa, razão pela qual resolveu indiciá- lo pela prática de crime de posse de arma de fogo de uso permitido, infração de médio potencial ofensivo, punida com pena de detenção de 01 a 03 anos e multa. No curso das investigações, requereu ao Judiciário interceptação telefônica da linha do aparelho celular de Lucas para melhor investigar a prática do crime mencionado, tendo sido o pedido deferido. De acordo com a situação narrada, a prova oriunda da interceptação deve ser considerada', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 68, 'A', 'ilícita, pois somente o Ministério Público tem legitimidade para representar pela medida.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 68, 'B', 'válida, desde que tenha sido deferida por ordem do juiz competente para ação principal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 68, 'C', 'ilícita, pois o crime investigado é punido com detenção.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 68, 'D', 'ilícita, assim como as dela derivadas, ainda que estas pudessem ser obtidas por fonte independente da primeira.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 69, 'No dia 10 de maio de 2015, Maria, 25 anos, foi vítima de um crime de estupro simples, mas, traumatizada, não mostrou interesse em dar início a qualquer investigação penal ou ação penal em relação aos fatos. Os pais de Maria, porém, requerem a instauração de inquérito policial para apurar autoria, entendendo que, após identificar o agente, Maria poderá decidir melhor sobre o interesse na persecução penal. Foi proferido despacho indeferindo o requerimento de abertura de inquérito. Considerando a situação narrada, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 69, 'A', 'Do despacho que indefere o requerimento de abertura de inquérito policial não cabe qualquer recurso, administrativo ou judicial.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 69, 'B', 'Em que pese o interesse de Maria ser relevante para o início da ação penal, a instauração de inquérito policial independe de sua representação.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 69, 'C', 'Caso Maria manifeste interesse na instauração de inquérito policial após o indeferimento, ainda dentro do prazo decadencial, o procedimento poderá ter início, independentemente do surgimento de novas provas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 69, 'D', 'Apesar de os pais de Maria não poderem requerer a instauração de inquérito policial, o Ministério Público pode requisitar o início do procedimento na hipótese, tendo em vista a natureza pública da ação.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 70, 'Em normas coletivas firmadas pela empresa Montagens Industriais Ltda., há previsão de multa por descumprimento de cláusulas normativas que foram efetivamente descumpridas pela empresa. Diante disso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 70, 'A', 'O empregado terá de ajuizar tantas ações quantas forem as lesões, postulando, em cada uma delas, a multa pelo descumprimento de obrigações previstas nas respectivas cláusulas.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 70, 'B', 'Tratando-se de multa prevista em instrumento normativo em decorrência de descumprimento de cláusula que reproduz texto de lei, a multa é incabível.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 70, 'C', 'Tendo em vista a reprodução de textos de lei em cláusulas de instrumentos normativos diversos, ficará a critério do juiz definir se pode haver esse acúmulo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 70, 'D', 'O descumprimento de qualquer cláusula constante de instrumentos normativos diversos não submete o empregado a ajuizar várias ações, pleiteando, em cada uma, o pagamento de multa referente ao descumprimento de obrigações previstas nas respectivas cláusulas.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 71, 'Plácido, empregado de um restaurante, sem qualquer motivo, passou a agredir verbalmente seu superior, até que, violentamente, quebrou uma mesa e uma cadeira que estavam próximas. Contornada a situação, Plácido foi dispensado e a empresa descontou no seu TRCT os valores do prejuízo com os móveis, que correspondiam a 60% do salário do trabalhador. Sobre o episódio apresentado, assinale a opção correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 71, 'A', 'A empresa pode descontar o valor mesmo sem previsão contratual para tanto, pois a atitude de Plácido, ao praticar o dano, foi dolosa.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 71, 'B', 'O desconto na remuneração do empregado relativo ao dano causado é vedado em qualquer hipótese.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 71, 'C', 'A empresa só poderia descontar o valor do dano causado por Plácido se houvesse previsão contratual nesse sentido.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 71, 'D', 'Não estando a parcela relacionada a um desconto tipificado em lei, não pode haver o desconto nas verbas devidas a Plácido.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 72, 'Jefferson, empregado da sociedade empresária Editora Século XIX, resolve adotar, sozinho, uma criança de 5 anos de idade. Diante da hipótese, de acordo com a legislação em vigor, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 72, 'A', 'Jefferson não terá direito à licença-maternidade, por falta de dispositivo legal.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 72, 'B', 'Jefferson receberá salário-maternidade, mas não terá direito à licença-maternidade, porque a regra aplica-se apenas às mulheres.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 72, 'C', 'Jefferson terá direito à licença-maternidade, pois este direito aplica-se à pessoa que adotar, independentemente do gênero.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 72, 'D', 'Jefferson terá direito à licença-maternidade de 60 dias.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 73, 'Nelson foi contratado como vigilante, diretamente pelo Banco Moeda Firme, empresa que assinou a sua carteira profissional. Ele atua em diversas agências bancárias e recebe adicional de periculosidade em seu contracheque. Sobre a categoria profissional de Nelson e em relação ao adicional de periculosidade, assinale a opção correta de acordo com a jurisprudência do TST.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 73, 'A', 'Nelson não é bancário.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 73, 'B', 'O recebimento do adicional de periculosidade é uma liberalidade do empregador.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 73, 'C', 'Nelson integra a categoria dos bancários, já que seu empregador explora essa atividade.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 73, 'D', 'A situação é irregular, pois o serviço de vigilante precisa ser terceirizado.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 74, 'Reinaldo trabalha em uma empresa cujo regulamento interno prevê que o empregador pagará a conta de telefone celular do empregado, até o limite de R$ 150,00 mensais. Posteriormente, havendo crise no setor em que a empresa atua, o regulamento interno foi expressamente alterado para constar que, dali em diante, a empresa arcará com a conta dos celulares dos empregados até o limite de R$ 50,00 mensais. De acordo com o entendimento consolidado do TST, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 74, 'A', 'O regulamento interno é ato unilateral de vontade do empregador, que poderá modificá-lo a qualquer momento, daí por que não há direito adquirido e a nova condição alcança Reinaldo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 74, 'B', 'A alteração somente é válida para aqueles que foram admitidos anteriormente à mudança e não prevalece para os que forem contratados após a mudança.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 74, 'C', 'A alteração é válida, mas só alcança aqueles admitidos posteriormente à mudança, não podendo então alcançar a situação de Reinaldo.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 74, 'D', 'A alteração feita pela empresa é ilegal, pois, uma vez concedida a benesse, ela não pode ser retirada em momento algum e para nenhum empregado, atual ou futuro.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 75, 'Jorge, Luiz e Pedro trabalham na mesma empresa. Na época designada para o gozo das férias, eles foram informados pelo empregador que Jorge não teria direito às férias porque havia faltado, injustificadamente, 34 dias ao longo do período aquisitivo; que Luiz teria que fracionar as férias em três períodos de 10 dias e que Pedro deveria converter 2/3 das férias em abono pecuniário, podendo gozar de apenas 1/3 destas, em razão da necessidade de serviço do setor de ambos. Diante disso, assinale a afirmativa correta.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 75, 'A', 'A informação do empregador foi correta nos três casos.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 75, 'B', 'Apenas no caso de Jorge o empregador está correto.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 75, 'C', 'O empregador agiu corretamente nos casos de Jorge e de Luiz, mas não no de Pedro.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 75, 'D', 'O empregador está errado nas três hipóteses.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 76, 'A empresa XPTO Ltda., necessitando dispensar empregado estável, ajuizou inquérito para apuração de falta grave em face de seu empregado. No dia da audiência, a empresa apresentou seis testemunhas, protestando pela oitiva de todas. O empregado apresentou três testemunhas, afirmando ser este o limite na Justiça do Trabalho. Assinale a alternativa que mostra qual advogado agiu da forma determinada na CLT.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 76, 'A', 'O advogado da empresa agiu corretamente, pois trata-se de inquérito para apuração de falta grave.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 76, 'B', 'O juiz determinou que a empresa dispensasse três das seis testemunhas, pois é necessário o equilíbrio com a outra parte. Logo, ambos os advogados agiram corretamente, levando o número de testemunhas que entendiam cabível.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 76, 'C', 'O advogado do empregado está correto, pois o limite de testemunhas para o processo de rito ordinário é de três para cada parte.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 76, 'D', 'Os dois advogados se equivocaram, pois o limite legal é de três por processo no rito ordinário, sendo as testemunhas do juízo.');
insert into questoes (num_exame, num_questao, enunciado, opcao_correta)
values(18, 77, 'Em ação trabalhista, a parte ré recebeu a notificação da sentença em um sábado. Assinale a opção que, de acordo com a CLT, indica o dia a partir do qual se iniciará a contagem do prazo recursal.', 'A');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
values(18, 77, 'A', 'O início do prazo será na segunda-feira e a contagem do prazo deverá ser iniciada na terça-feira, se forem dias úteis.');
insert into opcoes (num_exame, num_questao, letra_opcao, texto)
